import React, { useState } from "react";
import {
  Home,
  Cpu,
  Activity,
  ToggleLeft,
  ToggleRight,
  Zap,
  Battery,
  FileText,
  ChevronDown,
  ChevronUp,
} from "lucide-react";

const menuItems = [
  { label: "Digital Sensors", icon: <Cpu size={18} /> },
  { label: "Analog Sensors", icon: <Activity size={18} /> },
  { label: "Switch Sensors", icon: <ToggleLeft size={18} /> },
  { label: "Internal Relays", icon: <ToggleRight size={18} /> },
  { label: "External Relays", icon: <Zap size={18} /> },
  { label: "Power Sensors", icon: <Battery size={18} /> },
];

const reportItems = [
  { label: "Daily Report", icon: <FileText size={16} /> },
  { label: "Weekly Report", icon: <FileText size={16} /> },
  { label: "Monthly Report", icon: <FileText size={16} /> },
];

const Sidebar = ({
  onSensorSelect,
  onReportSelect,
  onWelcomeSelect,
  activeSensorType,
  selectedPage,
  selectedReport,
}) => {
  const [reportsOpen, setReportsOpen] = useState(false);

  return (
    <aside className="w-64 bg-gray-800 text-white flex flex-col justify-between shadow-lg">
      {/* Header */}
      <div className="p-4 border-b border-gray-700">
        <h1 className="text-xl font-bold tracking-wide">Sensorium</h1>
      </div>

      {/* Nav */}
      <nav className="flex-1 overflow-y-auto">
        <ul className="space-y-1 p-2">
          {/* Welcome Page */}
          <li>
            <button
              className={`flex items-center w-full px-3 py-2 rounded-md text-sm font-medium transition ${
                selectedPage === "welcome"
                  ? "bg-gray-700 text-white"
                  : "text-gray-300 hover:bg-gray-700 hover:text-white"
              }`}
              onClick={onWelcomeSelect}
            >
              <Home size={18} className="mr-2" />
              <span>Welcome</span>
            </button>
          </li>

          {/* Sensor Menu */}
          {menuItems.map(({ label, icon }) => (
            <li key={label}>
              <button
                className={`flex items-center w-full px-3 py-2 rounded-md text-sm font-medium transition ${
                  activeSensorType === label
                    ? "bg-gray-700 text-white"
                    : "text-gray-300 hover:bg-gray-700 hover:text-white"
                }`}
                onClick={() => onSensorSelect(label)}
              >
                <span className="mr-2">{icon}</span>
                {label}
              </button>
            </li>
          ))}

          {/* Reports Dropdown */}
          <li>
            <button
              className={`flex items-center justify-between w-full px-3 py-2 rounded-md text-sm font-medium transition ${
                selectedPage === "reports"
                  ? "bg-gray-700 text-white"
                  : "text-gray-300 hover:bg-gray-700 hover:text-white"
              }`}
              onClick={() => setReportsOpen(!reportsOpen)}
            >
              <div className="flex items-center">
                <FileText size={18} className="mr-2" />
                <span>Reports</span>
              </div>
              {reportsOpen ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
            </button>

            {reportsOpen && (
              <ul className="mt-1 ml-6 space-y-1">
                {reportItems.map(({ label, icon }) => (
                  <li key={label}>
                    <button
                      className={`flex items-center w-full px-3 py-2 rounded-md text-sm transition ${
                        selectedPage === "reports" && selectedReport === label
                          ? "bg-gray-700 text-white"
                          : "text-gray-300 hover:bg-gray-700 hover:text-white"
                      }`}
                      onClick={() => onReportSelect(label)}
                    >
                      <span className="mr-2">{icon}</span>
                      {label}
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </li>
        </ul>
      </nav>

      {/* Footer */}
      <div className="p-3 text-xs text-gray-400 border-t border-gray-700 text-center">
        <p>&copy; 2025 GMS</p>
      </div>
    </aside>
  );
};

export default Sidebar;
