import React, { useState } from "react";
import {
  Cpu,
  Activity,
  ToggleLeft,
  ToggleRight,
  Zap,
  Battery,
  FileText,
  ChevronDown,
  ChevronUp,
} from "lucide-react";
import "./Sidebar.css";

const menuItems = [
  { label: "Digital Sensors", key: "digital-sensors", icon: <Cpu size={18} /> },
  { label: "Analog Sensors", key: "analog-sensors", icon: <Activity size={18} /> },
  { label: "Switch Sensors", key: "switch-sensors", icon: <ToggleLeft size={18} /> },
  { label: "Internal Relays", key: "internal-relays", icon: <ToggleRight size={18} /> },
  { label: "External Relays", key: "external-relays", icon: <Zap size={18} /> },
  { label: "Power Sensors", key: "power-sensors", icon: <Battery size={18} /> },
];


const reportItems = [
  { label: "Daily Report", icon: <FileText size={16} /> },
  { label: "Weekly Report", icon: <FileText size={16} /> },
  { label: "Monthly Report", icon: <FileText size={16} /> },
];

const Sidebar = ({ onPageSelect, onReportSelect, activePage }) => {
  const [reportsOpen, setReportsOpen] = useState(false);

  return (
    <aside className="sidebar-container">
      <div className="sidebar-header">
        <h1 className="logo">Sensorium</h1>
      </div>

      <nav className="sidebar-nav">
        <ul className="sidebar-menu">
          {menuItems.map(({ label, icon }) => (
            <li key={label} className="sidebar-item">
              <button
                className={`sidebar-button ${
                  activePage === label ? "active" : ""
                }`}
                onClick={() => onPageSelect(label)}
              >
                {icon}
                <span className="sidebar-label">{label}</span>
              </button>
            </li>
          ))}

          {/* Reports Dropdown */}
          <li className="sidebar-item">
            <button
              className="sidebar-button"
              onClick={() => setReportsOpen(!reportsOpen)}
            >
              <FileText size={18} />
              <span className="sidebar-label">Reports</span>
              {reportsOpen ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
            </button>

            {reportsOpen && (
              <ul className="sidebar-submenu">
                {reportItems.map(({ label, icon }) => (
                  <li key={label} className="sidebar-subitem">
                    <button
                      className={`sidebar-subbutton ${
                        activePage === label ? "active" : ""
                      }`}
                      onClick={() => onReportSelect(label)}
                    >
                      {icon}
                      <span>{label}</span>
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </li>
        </ul>
      </nav>

      <div className="sidebar-footer">
        <p>&copy; 2025 GMS</p>
      </div>
    </aside>
  );
};

export default Sidebar;
