import React, { useState, useEffect } from 'react';
import { fetchLiveDevices } from '../services/api';

const menuItems = [
  'Digital Sensors',
  'Switch Sensors',
  'Internal Relays',
  'External Relays',
  'Power Sensors',
  'Debug Info',
];

const Sidebar = ({ onSensorSelect }) => {
  const [devices, setDevices] = useState([]);
  const [activeDevice, setActiveDevice] = useState(null);

  useEffect(() => {
    fetchLiveDevices()
      .then(data => setDevices(data))
      .catch(error => console.error("Error fetching devices:", error));
  }, []);

  const toggleDeviceMenu = (deviceId) => {
    setActiveDevice(activeDevice === deviceId ? null : deviceId);
  };

  return (
    <aside className="sidebar">
      <div className="logo-section">
        <h1 className="logo">Dashboard</h1>
      </div>
      <nav className="navigation">
        {devices.length > 0 ? (
          devices.map(device => (
            <div key={device.deviceId} className="device-menu-item">
              <button 
                className={`device-header ${activeDevice === device.deviceId ? 'active' : ''}`}
                onClick={() => toggleDeviceMenu(device.deviceId)}
              >
                {device.deviceId}
                <i className={`arrow-icon ${activeDevice === device.deviceId ? 'up' : 'down'}`}></i>
              </button>
              <ul className={`sub-menu ${activeDevice === device.deviceId ? 'visible' : ''}`}>
                {menuItems.map(item => (
                  <li 
                    key={item} 
                    className="sub-menu-item"
                    onClick={() => onSensorSelect(device.deviceId, item.replace(' ', ''))}
                  >
                    {item}
                  </li>
                ))}
              </ul>
            </div>
          ))
        ) : (
          <p className="loading-text">Loading devices...</p>
        )}
      </nav>
    </aside>
  );
};

export default Sidebar;
