import React from 'react';

const AllSensorsDisplay = ({ allSensorsData, deviceInfo, selectedSensor }) => {
  if (!allSensorsData || allSensorsData.length === 0) {
    return (
      <div className="bg-white rounded-xl shadow p-6 text-center text-gray-600">
        No data found for {selectedSensor.toLowerCase()}.
      </div>
    );
  }

  const sensorsByDevice = allSensorsData.reduce((acc, sensor) => {
    const { deviceId } = sensor;
    if (!acc[deviceId]) acc[deviceId] = [];
    acc[deviceId].push(sensor);
    return acc;
  }, {});

  const deviceInfoMap = deviceInfo.reduce((acc, dev) => {
    acc[dev.deviceId] = dev;
    return acc;
  }, {});

  const renderValue = (value) =>
    value !== null && value !== undefined ? value.toFixed(1) : 'N/A';

  const renderSensorCard = (sensor, index) => (
    <div
      key={index}
      className="bg-white rounded-xl shadow-md border border-gray-200 p-6"
    >
      {/* Header */}
      <div className="flex items-center space-x-2 mb-4">
        <span className="text-green-500 text-lg">●</span>
        <h3 className="text-lg font-semibold text-blue-600">
          {sensor.label || `Sensor ${sensor.sensorId}`}
        </h3>
      </div>

      {/* Digital Sensors */}
      {sensor.temperature !== undefined && sensor.humidity !== undefined && (
        <>
          <div className="mb-3">
            <p className="text-gray-600 text-sm">Temperature</p>
            <div className="flex items-center justify-between">
              <span className="text-xl font-bold text-gray-900">
                {renderValue(sensor.temperature)} °C
              </span>
              <div className="flex flex-col text-xs text-right">
                <span className="text-red-500">
                  ▲ {renderValue(sensor.temperatureHigh)} °C
                </span>
                <span className="text-blue-500">
                  ▼ {renderValue(sensor.temperatureLow)} °C
                </span>
              </div>
            </div>
          </div>

          <div className="mb-3">
            <p className="text-gray-600 text-sm">Humidity</p>
            <div className="flex items-center justify-between">
              <span className="text-xl font-bold text-gray-900">
                {renderValue(sensor.humidity)} %RH
              </span>
              <div className="flex flex-col text-xs text-right">
                <span className="text-red-500">
                  ▲ {renderValue(sensor.humidityHigh)} %RH
                </span>
                <span className="text-blue-500">
                  ▼ {renderValue(sensor.humidityLow)} %RH
                </span>
              </div>
            </div>
          </div>

          {sensor.heatIndex !== undefined && (
            <div className="mb-3">
              <p className="text-gray-600 text-sm">Heat Index</p>
              <div className="flex items-center justify-between">
                <span className="text-xl font-bold text-gray-900">
                  {renderValue(sensor.heatIndex)} °C
                </span>
                <div className="flex flex-col text-xs text-right">
                  <span className="text-red-500">
                    ▲ {renderValue(sensor.heatIndexHigh)} °C
                  </span>
                  <span className="text-blue-500">
                    ▼ {renderValue(sensor.heatIndexLow)} °C
                  </span>
                </div>
              </div>
            </div>
          )}

          {sensor.dewPoint !== undefined && (
            <div>
              <p className="text-gray-600 text-sm">Dew Point</p>
              <span className="text-xl font-bold text-gray-900">
                {renderValue(sensor.dewPoint)} °C
              </span>
            </div>
          )}
        </>
      )}

      {/* Other sensor types */}
      {selectedSensor === 'Switch Sensors' && sensor.state !== undefined && (
        <div>
          <p className="text-gray-600 text-sm">State</p>
          <span className="text-xl font-bold text-gray-900">
            {sensor.enabled === true ? 'OPEN' : 'CLOSED'}
          </span>
        </div>
      )}

      {selectedSensor === 'Power Sensors' && sensor.connected !== undefined && (
        <>
          <div>
            <p className="text-gray-600 text-sm">Status</p>
            <span className="text-xl font-bold text-gray-900">
              {sensor.connected === 1 ? 'Connected' : 'Disconnected'}
            </span>
          </div>
          <div>
            <p className="text-gray-600 text-sm">State</p>
            <span className="text-xl font-bold text-gray-900">
              {sensor.state === 1 ? 'ON' : 'OFF'}
            </span>
          </div>
        </>
      )}

      {(selectedSensor === 'Internal Relays' ||
        selectedSensor === 'External Relays') &&
        sensor.state !== undefined && (
          <div>
            <p className="text-gray-600 text-sm">State</p>
            <span className="text-xl font-bold text-gray-900">
              {sensor.state === 1 ? 'ON' : 'OFF'}
            </span>
          </div>
        )}
    </div>
  );

  return (
    <div>
      {Object.entries(sensorsByDevice).map(([deviceId, sensors]) => {
        const devInfo = deviceInfoMap[deviceId] || {};

        return (
          <div key={deviceId} className="mb-10">
            {/* Device Header */}
            <h2 className="text-xl font-semibold text-gray-800 mb-4 border-b-2 border-blue-500 inline-block pb-1">
              {devInfo.deviceName || `Device ${deviceId}`}
            </h2>

            {/* Device Info Card (styled like screenshot) */}
            <div className="bg-white rounded-xl shadow-md border border-gray-200 p-6 mb-6 max-w-3xl">
              <div className="space-y-2 text-sm">
                <p>
                  <span className="font-semibold text-gray-800">Serial Number:</span>{' '}
                  {devInfo.serialNumber}
                </p>
                <p>
                  <span className="font-semibold text-gray-800">Firmware Version:</span>{' '}
                  {devInfo.firmwareVersion}
                </p>
                <p>
                  <span className="font-semibold text-gray-800">IPv4 Address:</span>{' '}
                  {devInfo.ipv4Address}
                </p>
                <p>
                  <span className="font-semibold text-gray-800">MAC Address:</span>{' '}
                  {devInfo.macAddress}
                </p>
                <p>
                  <span className="font-semibold text-gray-800">Port:</span>{' '}
                  {devInfo.httpPort}
                </p>
              </div>
            </div>

            {/* Sensor Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {sensors.map((sensor, index) => renderSensorCard(sensor, index))}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default AllSensorsDisplay;
