import React from 'react';
import './AllSensorsDisplay.css';

const AllSensorsDisplay = ({ allSensorsData, deviceInfo, selectedSensor }) => {
  if (!allSensorsData || allSensorsData.length === 0) {
    return <div className="loading-state-card">No data found for {selectedSensor.toLowerCase()}.</div>;
  }
  
  const sensorsByDevice = allSensorsData.reduce((acc, sensor) => {
    const { deviceId } = sensor;
    if (!acc[deviceId]) {
      acc[deviceId] = [];
    }
    acc[deviceId].push(sensor);
    return acc;
  }, {});

  const deviceInfoMap = deviceInfo.reduce((acc, dev) => {
    acc[dev.deviceId] = dev;
    return acc;
  }, {});

  const renderDigitalSensorCard = (sensor) => {
    return (
      <>
        {sensor.temperature !== undefined && sensor.temperature !== null && (
          <div className="data-section">
            <div className="data-label">Temperature</div>
            <div className="data-values">
              <span className="main-value">{sensor.temperature.toFixed(1)}°C</span>
              <div className="range-values">
                <span className="high-value">▲ {sensor.temperatureHigh.toFixed(1)}°C</span>
                <span className="low-value">▼ {sensor.temperatureLow.toFixed(1)}°C</span>
              </div>
            </div>
          </div>
        )}
        {sensor.humidity !== undefined && sensor.humidity !== null && (
          <div className="data-section">
            <div className="data-label">Humidity</div>
            <div className="data-values">
              <span className="main-value">{sensor.humidity.toFixed(1)} %RH</span>
              <div className="range-values">
                <span className="high-value">▲ {sensor.humidityHigh.toFixed(1)} %RH</span>
                <span className="low-value">▼ {sensor.humidityLow.toFixed(1)} %RH</span>
              </div>
            </div>
          </div>
        )}
        {sensor.heatIndex !== undefined && sensor.heatIndex !== null && (
          <div className="data-section">
            <div className="data-label">Heat Index</div>
            <div className="data-values">
              <span className="main-value">{sensor.heatIndex.toFixed(1)}°C</span>
            </div>
          </div>
        )}
        {sensor.dewPoint !== undefined && sensor.dewPoint !== null && (
          <div className="data-section">
            <div className="data-label">Dew Point</div>
            <div className="data-values">
              <span className="main-value">{sensor.dewPoint.toFixed(1)}°C</span>
            </div>
          </div>
        )}
      </>
    );
  };

  const renderSwitchSensorCard = (sensor) => {
    const statusText = sensor.state === 1 ? 'OPEN' : 'CLOSED';
    const statusClass = sensor.state === 1 ? 'status-open' : 'status-closed';
    return (
      <div className="data-section">
        <div className="data-label">Status</div>
        <div className="data-values">
          <span className={`main-value ${statusClass}`}>{statusText}</span>
        </div>
      </div>
    );
  };
  
  const renderRelaySensorCard = (sensor) => {
    const statusText = sensor.state === 1 ? 'ON' : 'OFF';
    const statusClass = sensor.state === 1 ? 'status-on' : 'status-off';
    return (
      <div className="data-section">
        <div className="data-label">State</div>
        <div className="data-values">
          <span className={`main-value ${statusClass}`}>{statusText}</span>
        </div>
      </div>
    );
  };

  const renderPowerSensorCard = (sensor) => {
    const connectedStatus = sensor.connected === 1 ? 'Connected' : 'Disconnected';
    const stateStatus = sensor.state === 1 ? 'ON' : 'OFF';
    return (
      <>
        <div className="data-section">
          <div className="data-label">Connection</div>
          <div className="data-values">
            <span className="main-value">{connectedStatus}</span>
          </div>
        </div>
        <div className="data-section">
          <div className="data-label">Power State</div>
          <div className="data-values">
            <span className="main-value">{stateStatus}</span>
          </div>
        </div>
      </>
    );
  };

  return (
    <div>
      {Object.entries(sensorsByDevice).map(([deviceId, sensors]) => {
        const devInfo = deviceInfoMap[deviceId] || {};
        
        return (
          <div key={deviceId} className="device-section">
            <div className="device-info-container">
              <h2>{devInfo.deviceName || `Device ${deviceId}`}</h2>
              <div className="device-details">
                <p><strong>Serial Number:</strong> {devInfo.serialNumber}</p>
                <p><strong>Firmware Version:</strong> {devInfo.firmwareVersion}</p>
                <p><strong>IPv4 Address:</strong> {devInfo.ipv4Address}</p>
                <p><strong>MAC Address:</strong> {devInfo.macAddress}</p>
                <p><strong>Port:</strong> {devInfo.httpPort}</p>
              </div>
            </div>

            <div className="sensor-details-container">
              {sensors.map((sensor, index) => (
                <div key={index} className="sensor-card">
                  <div className="card-header">
                    <span className={`status-icon ${sensor.connected === 1 ? 'connected-icon' : 'disconnected-icon'}`}></span>
                    <h3 className="card-title">{sensor.label || `Sensor ${sensor.sensorId}`}</h3>
                  </div>
                  {selectedSensor === 'Digital Sensors' && renderDigitalSensorCard(sensor)}
                  {selectedSensor === 'Switch Sensors' && renderSwitchSensorCard(sensor)}
                  {(selectedSensor === 'Internal Relays' || selectedSensor === 'External Relays') && renderRelaySensorCard(sensor)}
                  {selectedSensor === 'Power Sensors' && renderPowerSensorCard(sensor)}
                </div>
              ))}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default AllSensorsDisplay;
