import React, { useEffect, useState } from "react";
import axios from "axios";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import {
  Thermometer,
  Droplets,
  Activity,
  Cloud,
  Calendar,
  Download,
} from "lucide-react";
import SensorLoadingScreen from "../Loading/SensorLoadingScreen";

const ReportPage = ({ period }) => {
  const [devices, setDevices] = useState([]);
  const [sensors, setSensors] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState("");
  const [selectedSensor, setSelectedSensor] = useState("");
  const [reportData, setReportData] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch devices
  useEffect(() => {
    axios.get("https://sensorium-api.vercel.app/api/devices").then((res) => {
      setDevices(res.data);
    });
  }, []);

  // Fetch sensors when device changes
  useEffect(() => {
    if (selectedDevice) {
      axios
        .get(`https://sensorium-api.vercel.app/api/sensors/${selectedDevice}`)
        .then((res) => {
          setSensors(res.data);
          setSelectedSensor("");
        });
    } else {
      setSensors([]);
      setSelectedSensor("");
    }
  }, [selectedDevice]);

  const periodMap = {
    "Daily Report": "daily",
    "Weekly Report": "weekly",
    "Monthly Report": "monthly",
  };

  // Fetch report
  useEffect(() => {
    if (selectedDevice && selectedSensor && period) {
      const backendPeriod = periodMap[period];
      if (!backendPeriod) {
        setReportData([]);
        return;
      }
      setLoading(true);
      axios
        .get(
          `https://sensorium-api.vercel.app/api/reports?deviceId=${selectedDevice}&sensorId=${selectedSensor}&period=${backendPeriod}`
        )
        .then((res) => setReportData(res.data))
        .catch((err) => {
          console.error("Error fetching report:", err);
          setReportData([]);
        })
        .finally(() => setLoading(false));
    }
  }, [selectedDevice, selectedSensor, period]);

  const formatPeriodLabel = (date) => {
    const d = new Date(date);
    if (period === "Daily Report") {
      return d.toLocaleString([], {
        year: "numeric",
        month: "2-digit",
        day: "2-digit",
        hour: "2-digit",
        minute: "2-digit",
      });
    }
    return d.toLocaleDateString();
  };

  // Export Excel
  const handleDownloadExcel = () => {
    if (!reportData.length) return;

    const formattedData = reportData.map((row) => ({
      Period: formatPeriodLabel(row.period),
      "Min Temp": row.minTemp !== null ? row.minTemp.toFixed(2) : "-",
      "Avg Temp": row.avgTemp !== null ? row.avgTemp.toFixed(2) : "-",
      "Max Temp": row.maxTemp !== null ? row.maxTemp.toFixed(2) : "-",
      "Avg Humidity": row.avgHumidity !== null ? row.avgHumidity.toFixed(2) : "-",
      "Avg Heat Index":
        row.avgHeatIndex !== null ? row.avgHeatIndex.toFixed(2) : "-",
      "Avg Dew Point": row.avgDewPoint !== null ? row.avgDewPoint.toFixed(2) : "-",
    }));

    const worksheet = XLSX.utils.json_to_sheet(formattedData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, "Report");

    const excelBuffer = XLSX.write(workbook, { bookType: "xlsx", type: "array" });
    const blob = new Blob([excelBuffer], { type: "application/octet-stream" });

    saveAs(blob, `Report_${period}_${selectedDevice}_${selectedSensor}.xlsx`);
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="flex flex-col md:flex-row justify-between items-center mb-6">
        <h2 className="flex items-center text-2xl font-bold text-gray-800">
          <Calendar className="w-6 h-6 text-blue-600 mr-2" />
          Reports - {period}
        </h2>

        {/* Filters */}
        <div className="flex items-center space-x-3 mt-4 md:mt-0">
          <select
            className="px-3 py-2 border border-gray-300 rounded-lg text-sm shadow-sm focus:ring focus:ring-blue-200"
            value={selectedDevice}
            onChange={(e) => setSelectedDevice(e.target.value)}
          >
            <option value="">Select Device</option>
            {devices.map((d) => (
              <option key={d.deviceId} value={d.deviceId}>
                {d.deviceName}
              </option>
            ))}
          </select>

          <select
            className="px-3 py-2 border border-gray-300 rounded-lg text-sm shadow-sm focus:ring focus:ring-blue-200"
            value={selectedSensor}
            onChange={(e) => setSelectedSensor(e.target.value)}
            disabled={!selectedDevice}
          >
            <option value="">Select Sensor</option>
            {sensors.map((s) => (
              <option key={s.sensorId} value={s.sensorId}>
                {s.label || `Sensor ${s.sensorId}`}
              </option>
            ))}
          </select>

          {reportData.length > 0 && (
            <button
              onClick={handleDownloadExcel}
              className="flex items-center bg-blue-600 text-white px-3 py-2 rounded-lg shadow-sm hover:bg-blue-700 transition"
              title="Download Excel"
            >
              <Download className="w-4 h-4 mr-1" />
              Download
            </button>
          )}
        </div>
      </div>

      {/* Content */}
      {loading ? (
        <div className="flex justify-center items-center h-64">
          <SensorLoadingScreen />
        </div>
      ) : reportData.length > 0 ? (
        <>
          {/* Table */}
          <h3 className="text-lg font-semibold text-gray-700 mb-3">
            Average Report
          </h3>
          <div className="overflow-x-auto bg-white shadow rounded-lg mb-6">
            <table className="w-full border-collapse text-sm">
              <thead className="bg-gray-100 text-gray-700 text-left">
                <tr>
                  <th className="px-4 py-2 font-medium">
                    <Calendar className="inline w-4 h-4 mr-1" /> Period
                  </th>
                  <th className="px-4 py-2 font-medium">
                    <Thermometer className="inline w-4 h-4 mr-1" /> Min Temp
                  </th>
                  <th className="px-4 py-2 font-medium">
                    <Thermometer className="inline w-4 h-4 mr-1" /> Avg Temp
                  </th>
                  <th className="px-4 py-2 font-medium">
                    <Thermometer className="inline w-4 h-4 mr-1" /> Max Temp
                  </th>
                  <th className="px-4 py-2 font-medium">
                    <Droplets className="inline w-4 h-4 mr-1" /> Avg Humidity
                  </th>
                  <th className="px-4 py-2 font-medium">
                    <Activity className="inline w-4 h-4 mr-1" /> Avg Heat Index
                  </th>
                  <th className="px-4 py-2 font-medium">
                    <Cloud className="inline w-4 h-4 mr-1" /> Avg Dew Point
                  </th>
                </tr>
              </thead>
              <tbody>
                {reportData.map((row, idx) => (
                  <tr
                    key={idx}
                    className="border-t border-gray-200 hover:bg-gray-50"
                  >
                    <td className="px-4 py-2">{formatPeriodLabel(row.period)}</td>
                    <td className="px-4 py-2">
                      {row.minTemp !== null ? row.minTemp.toFixed(2) : "-"}
                    </td>
                    <td className="px-4 py-2">
                      {row.avgTemp !== null ? row.avgTemp.toFixed(2) : "-"}
                    </td>
                    <td className="px-4 py-2">
                      {row.maxTemp !== null ? row.maxTemp.toFixed(2) : "-"}
                    </td>
                    <td className="px-4 py-2">
                      {row.avgHumidity !== null ? row.avgHumidity.toFixed(2) : "-"}
                    </td>
                    <td className="px-4 py-2">
                      {row.avgHeatIndex !== null
                        ? row.avgHeatIndex.toFixed(2)
                        : "-"}
                    </td>
                    <td className="px-4 py-2">
                      {row.avgDewPoint !== null ? row.avgDewPoint.toFixed(2) : "-"}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Chart */}
          <div className="bg-white shadow rounded-lg p-4">
            <ResponsiveContainer width="100%" height={400}>
              <LineChart data={reportData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                <XAxis
                  dataKey="period"
                  tickFormatter={formatPeriodLabel}
                  stroke="#6b7280"
                  fontSize={12}
                />
                <YAxis stroke="#6b7280" fontSize={12} />
                <Tooltip labelFormatter={formatPeriodLabel} />
                <Legend />
                <Line
                  type="monotone"
                  dataKey="avgTemp"
                  stroke="#3b82f6"
                  name="Avg Temp"
                  strokeWidth={2}
                />
                <Line
                  type="monotone"
                  dataKey="avgHumidity"
                  stroke="#10b981"
                  name="Avg Humidity"
                  strokeWidth={2}
                />
                <Line
                  type="monotone"
                  dataKey="avgHeatIndex"
                  stroke="#ef4444"
                  name="Avg Heat Index"
                  strokeWidth={2}
                />
                <Line
                  type="monotone"
                  dataKey="avgDewPoint"
                  stroke="#8b5cf6"
                  name="Avg Dew Point"
                  strokeWidth={2}
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </>
      ) : (
        <p className="text-gray-500 text-center mt-10">
          Select a device and sensor to view the report.
        </p>
      )}
    </div>
  );
};

export default ReportPage;
