import React, { useState } from "react";
import {
  Home,
  Cpu,
  Activity,
  ToggleLeft,
  ToggleRight,
  Zap,
  Battery,
  FileText,
  ChevronDown,
  ChevronUp,
} from "lucide-react";

// The menuItems and reportItems are outside the component and don't need changes
const menuItems = [
  { label: "Digital Sensors", icon: <Cpu size={18} /> },
  { label: "Analog Sensors", icon: <Activity size={18} /> },
  { label: "Switch Sensors", icon: <ToggleLeft size={18} /> },
  { label: "Internal Relays", icon: <ToggleRight size={18} /> },
  { label: "External Relays", icon: <Zap size={18} /> },
  { label: "Power Sensors", icon: <Battery size={18} /> },
];

const reportItems = [
  { label: "Daily Report", icon: <FileText size={16} /> },
  { label: "Weekly Report", icon: <FileText size={16} /> },
  { label: "Monthly Report", icon: <FileText size={16} /> },
];

const Sidebar = ({
  onSensorSelect,
  onReportSelect,
  onWelcomeSelect,
  activeSensorType,
  selectedPage,
  selectedReport,
}) => {
  const [reportsOpen, setReportsOpen] = useState(false);

  // Define a reusable class for the active state
  const activeClass =
    "bg-sky-700/50 text-sky-300 border-l-4 border-sky-400 font-semibold shadow-inner shadow-black/20";
  // Define a reusable class for the base/hover state
  const inactiveClass =
    "text-gray-400 hover:bg-gray-700/50 hover:text-white";

  // Reusable class for all buttons
  const baseButtonClass =
    "flex items-center w-full px-4 py-3 rounded-lg text-sm transition-all duration-300 transform hover:scale-[1.01]";

  return (
    <aside className="h-screen w-64 bg-gradient-to-b from-slate-900 to-indigo-950 text-gray-200 flex flex-col shadow-2xl shadow-indigo-900/50">
      {/* Header */}
      <div className="p-6 border-b border-indigo-800/50">
        <h1 className="text-2xl font-black text-sky-400 tracking-widest drop-shadow-lg">
          SENSORIOT
        </h1>
      </div>

      {/* Navigation */}
      <nav className="flex-1 overflow-y-auto">
        <ul className="space-y-2 px-3 pt-5">
          {/* Welcome */}
          <li>
            <button
              className={`${baseButtonClass} ${
                selectedPage === "welcome"
                  ? activeClass
                  : inactiveClass
              }`}
              onClick={onWelcomeSelect}
            >
              <Home size={20} className="mr-3" />
              <span>Dashboard Overview</span>
            </button>
          </li>

          {/* Sensor Items */}
          <li className="pt-2">
            <h3 className="text-xs uppercase tracking-wider text-gray-500 mb-2 px-4 font-semibold">
              Sensors & Devices
            </h3>
          </li>
          {menuItems.map(({ label, icon }) => (
            <li key={label}>
              <button
                className={`${baseButtonClass} ${
                  activeSensorType === label
                    ? activeClass
                    : inactiveClass
                }`}
                onClick={() => onSensorSelect(label)}
              >
                <span className="mr-3">{icon}</span>
                {label}
              </button>
            </li>
          ))}

          {/* Reports */}
          <li className="pt-4">
            <h3 className="text-xs uppercase tracking-wider text-gray-500 mb-2 px-4 font-semibold">
              Data & Reports
            </h3>
            <button
              className={`flex items-center justify-between w-full px-4 py-3 rounded-lg text-sm font-medium transition-all duration-300 transform hover:scale-[1.01] ${
                selectedPage === "reports" && !activeSensorType
                  ? activeClass
                  : inactiveClass
              }`}
              onClick={() => setReportsOpen(!reportsOpen)}
            >
              <div className="flex items-center">
                <FileText size={20} className="mr-3" />
                <span>Reports & Analytics</span>
              </div>
              {reportsOpen ? <ChevronUp size={18} /> : <ChevronDown size={18} />}
            </button>

            {reportsOpen && (
              <ul className="pl-6 mt-1 space-y-1 transition-all duration-300 ease-in-out">
                {reportItems.map(({ label, icon }) => (
                  <li key={label}>
                    <button
                      className={`flex items-center w-full px-4 py-2 rounded-lg text-sm transition-all duration-300 hover:scale-[1.01] ${
                        selectedPage === "reports" && selectedReport === label
                          ? "bg-sky-700/50 text-sky-300 font-medium border-l-2 border-sky-400"
                          : "text-gray-400 hover:bg-gray-700/50 hover:text-white"
                      }`}
                      onClick={() => onReportSelect(label)}
                    >
                      <span className="mr-2 opacity-75">{icon}</span>
                      {label}
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </li>
        </ul>
      </nav>

      {/* Footer */}
      <div className="p-4 border-t border-indigo-800/50 text-xs text-gray-500 text-center font-light">
        © 2025 Sensorium IoT.
        <br />
        System Status: <span className="text-green-400">Online</span>
      </div>
    </aside>
  );
};

export default Sidebar;
