import React from "react";

const AllSensorsDisplay = ({ allSensorsData, deviceInfo, selectedSensor }) => {
  if (!allSensorsData || allSensorsData.length === 0) {
    return (
      <div className="bg-gradient-to-r from-gray-100 to-gray-200 rounded-xl shadow-lg p-6 text-center text-gray-800 font-medium">
        No data found for <span className="font-semibold">{selectedSensor}</span>.
      </div>
    );
  }

  const sensorsByDevice = allSensorsData.reduce((acc, sensor) => {
    const { deviceId } = sensor;
    if (!acc[deviceId]) acc[deviceId] = [];
    acc[deviceId].push(sensor);
    return acc;
  }, {});

  const deviceInfoMap = deviceInfo.reduce((acc, dev) => {
    acc[dev.deviceId] = dev;
    return acc;
  }, {});

  const renderValue = (value) =>
    value !== null && value !== undefined ? value.toFixed(1) : "N/A";

  const renderSensorCard = (sensor, index) => (
    <div
      key={index}
      className="bg-gradient-to-tr from-white/90 via-gray-50/90 to-white/90 rounded-2xl shadow-md border border-gray-200 p-6 hover:shadow-2xl hover:scale-105 transform transition-all duration-300 relative overflow-hidden"
    >
      {/* Animated background effect */}
      <div className="absolute inset-0 bg-gradient-to-r from-cyan-200/20 via-blue-200/20 to-purple-200/20 opacity-30 animate-pulse rounded-2xl pointer-events-none"></div>

      {/* Header */}
      <div className="flex items-center space-x-2 mb-4 border-b pb-2 z-10 relative">
        <span
          className={`w-3 h-3 rounded-full ${
            sensor.active ? "bg-green-400 animate-ping" : "bg-gray-400"
          }`}
        ></span>
        <h3 className="text-lg font-semibold text-gray-800">
          {sensor.label || `Sensor ${sensor.sensorId}`}
        </h3>
      </div>

      {/* Digital Sensors */}
      {sensor.temperature !== undefined && sensor.humidity !== undefined && (
        <>
          <div className="mb-4 pb-3 border-b border-gray-200 z-10 relative">
            <p className="text-sm text-gray-700 font-medium">Temperature</p>
            <div className="flex items-center justify-between">
              <span className="text-xl font-bold text-gray-800 transition-all duration-300">
                {renderValue(sensor.temperature)} °C
              </span>
              <div className="flex flex-col text-xs text-right">
                <span className="text-red-500 font-semibold">
                  ▲ {renderValue(sensor.temperatureHigh)} °C
                </span>
                <span className="text-blue-500 font-semibold">
                  ▼ {renderValue(sensor.temperatureLow)} °C
                </span>
              </div>
            </div>
          </div>

          <div className="mb-4 pb-3 border-b border-gray-200 z-10 relative">
            <p className="text-sm text-gray-700 font-medium">Humidity</p>
            <div className="flex items-center justify-between">
              <span className="text-xl font-bold text-gray-800 transition-all duration-300">
                {renderValue(sensor.humidity)} %RH
              </span>
              <div className="flex flex-col text-xs text-right">
                <span className="text-red-500 font-semibold">
                  ▲ {renderValue(sensor.humidityHigh)} %RH
                </span>
                <span className="text-blue-500 font-semibold">
                  ▼ {renderValue(sensor.humidityLow)} %RH
                </span>
              </div>
            </div>
          </div>

          {sensor.heatIndex !== undefined && (
            <div className="mb-4 pb-3 border-b border-gray-200 z-10 relative">
              <p className="text-sm text-gray-700 font-medium">Heat Index</p>
              <div className="flex items-center justify-between">
                <span className="text-xl font-bold text-gray-800 transition-all duration-300">
                  {renderValue(sensor.heatIndex)} °C
                </span>
                <div className="flex flex-col text-xs text-right">
                  <span className="text-red-500 font-semibold">
                    ▲ {renderValue(sensor.heatIndexHigh)} °C
                  </span>
                  <span className="text-blue-500 font-semibold">
                    ▼ {renderValue(sensor.heatIndexLow)} °C
                  </span>
                </div>
              </div>
            </div>
          )}

          {sensor.dewPoint !== undefined && (
            <div className="z-10 relative">
              <p className="text-sm text-gray-700 font-medium">Dew Point</p>
              <span className="text-xl font-bold text-gray-800">
                {renderValue(sensor.dewPoint)} °C
              </span>
            </div>
          )}
        </>
      )}

      {/* Other sensor types */}
      {selectedSensor === "Switch Sensors" && sensor.state !== undefined && (
        <div className="z-10 relative">
          <p className="text-sm text-gray-700 font-medium">State</p>
          <span className="text-xl font-bold text-gray-800">
            {sensor.enabled === true ? "OPEN" : "CLOSED"}
          </span>
        </div>
      )}

      {selectedSensor === "Power Sensors" && sensor.connected !== undefined && (
        <>
          <div className="mb-4 pb-3 border-b border-gray-200 z-10 relative">
            <p className="text-sm text-gray-700 font-medium">Status</p>
            <span className="text-xl font-bold text-gray-800">
              {sensor.connected === 1 ? "Connected" : "Disconnected"}
            </span>
          </div>
          <div className="z-10 relative">
            <p className="text-sm text-gray-700 font-medium">State</p>
            <span className="text-xl font-bold text-gray-800">
              {sensor.state === 1 ? "ON" : "OFF"}
            </span>
          </div>
        </>
      )}

      {(selectedSensor === "Internal Relays" ||
        selectedSensor === "External Relays") &&
        sensor.state !== undefined && (
          <div className="z-10 relative">
            <p className="text-sm text-gray-700 font-medium">State</p>
            <span className="text-xl font-bold text-gray-800">
              {sensor.state === 1 ? "ON" : "OFF"}
            </span>
          </div>
        )}
    </div>
  );

  return (
    <div>
      {Object.entries(sensorsByDevice).map(([deviceId, sensors]) => {
        const devInfo = deviceInfoMap[deviceId] || {};

        return (
          <div key={deviceId} className="mb-10">
            {/* Device Header */}
            <h2 className="text-xl font-bold text-gray-800 mb-4 bg-gradient-to-r from-cyan-400 to-blue-500 bg-clip-text text-transparent border-b-2 border-gray-300 inline-block pb-1">
              {devInfo.deviceName || `Device ${deviceId}`}
            </h2>

            {/* Device Info Card */}
            <div className="bg-gradient-to-tr from-gray-50 via-gray-100 to-gray-50 rounded-xl shadow-md border border-gray-200 p-6 mb-6 max-w-3xl hover:shadow-2xl transition-shadow duration-300">
              <div className="space-y-2 text-sm text-gray-800">
                <p>
                  <span className="font-semibold">Serial Number:</span> {devInfo.serialNumber}
                </p>
                <p>
                  <span className="font-semibold">Firmware Version:</span> {devInfo.firmwareVersion}
                </p>
                <p>
                  <span className="font-semibold">IPv4 Address:</span> {devInfo.ipv4Address}
                </p>
                <p>
                  <span className="font-semibold">MAC Address:</span> {devInfo.macAddress}
                </p>
                <p>
                  <span className="font-semibold">Port:</span> {devInfo.httpPort}
                </p>
              </div>
            </div>

            {/* Sensor Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {sensors.map((sensor, index) => renderSensorCard(sensor, index))}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default AllSensorsDisplay;
