import React, { useEffect, useState } from "react";
import { Thermometer, Droplets, Activity, Server, AlertCircle } from "lucide-react";

// Base URL for the API
const API_BASE_URL = 'https://sensorium-api.vercel.app/api';

const App = () => {
  const [loading, setLoading] = useState(true);
  const [overviewData, setOverviewData] = useState({
    totalDevices: 0,
    activeSensors: 0,
    avgTemperature: null,
    avgHumidity: null,
    activeAlerts: 0,
  });
  const [sensorData, setSensorData] = useState([]);

  // Function to fetch all data from the APIs
  const fetchAllData = async () => {
    setLoading(true);
    try {
      // Fetch overview data (devices)
      const devicesResponse = await fetch(`${API_BASE_URL}/live/devices`);
      if (!devicesResponse.ok) {
        throw new Error(`Failed to fetch devices: ${devicesResponse.statusText}`);
      }
      const devices = await devicesResponse.json();

      // Fetch only digital sensor data as requested
      const digitalSensorsResponse = await fetch(`${API_BASE_URL}/live/digital-sensors`);
      if (!digitalSensorsResponse.ok) {
        throw new Error(`Failed to fetch digital sensors: ${digitalSensorsResponse.statusText}`);
      }
      
      const digitalSensors = await digitalSensorsResponse.json();

      // Set sensor data to only digital sensors
      setSensorData(digitalSensors);

      // Calculate aggregated values based on digital sensors only
      const totalDevices = devices.length;
      const activeSensors = digitalSensors.length;

      // Update the state with fetched data
      setOverviewData({
        totalDevices,
        activeSensors,
        avgTemperature: null, // No temperature data from digital sensors
        avgHumidity: null, // No humidity data from digital sensors
        activeAlerts: 2, // Placeholder as there's no clear API endpoint for alerts
      });
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Initial data fetch
    fetchAllData();

    // Set up auto-refresh every 5 seconds
    const intervalId = setInterval(() => {
      fetchAllData();
    }, 5000);

    // Clean up the interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  const statCards = [
    {
      title: "Total Devices",
      value: overviewData.totalDevices,
      icon: <Server size={32} className="text-blue-500" />,
      color: "blue",
    },
    {
      title: "Active Sensors",
      value: overviewData.activeSensors,
      icon: <Activity size={32} className="text-green-500" />,
      color: "green",
    },
    {
      title: "Avg. Temperature",
      value: overviewData.avgTemperature !== null ? `${overviewData.avgTemperature}°C` : "N/A",
      icon: <Thermometer size={32} className="text-red-500" />,
      color: "red",
    },
    {
      title: "Avg. Humidity",
      value: overviewData.avgHumidity !== null ? `${overviewData.avgHumidity}%` : "N/A",
      icon: <Droplets size={32} className="text-purple-500" />,
      color: "purple",
    },
    {
      title: "Active Alerts",
      value: overviewData.activeAlerts,
      icon: <AlertCircle size={32} className="text-yellow-500" />,
      color: "yellow",
    },
  ];

  return (
    <div className="bg-gray-100 min-h-screen p-8 font-sans">
      <script src="https://cdn.tailwindcss.com"></script>
      <div className="max-w-7xl mx-auto space-y-8">
        {/* Header */}
        <header className="bg-white rounded-xl shadow-sm p-6 sm:p-8 flex flex-col sm:flex-row items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-800">Welcome to Sensorium</h1>
            <p className="text-gray-500 mt-1">Your centralized hub for real-time sensor monitoring.</p>
          </div>
        </header>

        {/* Loading State */}
        {loading ? (
          <div className="text-center py-20">
            <h2 className="text-2xl font-semibold text-gray-700">Loading live data...</h2>
            <p className="text-gray-500 mt-2">This may take a moment.</p>
          </div>
        ) : (
          <>
            {/* System Overview Section */}
            <section>
              <h2 className="text-2xl font-semibold text-gray-700 mb-4">System Overview</h2>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-6">
                {statCards.map((card, index) => (
                  <div
                    key={index}
                    className="bg-white rounded-xl shadow-sm p-6 flex flex-col items-start"
                  >
                    <div className={`p-3 rounded-full bg-${card.color}-100`}>
                      {card.icon}
                    </div>
                    <p className="text-gray-500 mt-4 text-sm font-medium">{card.title}</p>
                    <h3 className="text-2xl font-bold text-gray-900 mt-1">{card.value}</h3>
                  </div>
                ))}
              </div>
            </section>

            {/* Detailed Sensor Cards Section */}
            <section>
              <h2 className="text-2xl font-semibold text-gray-700 mb-4">Live Digital Sensor Data</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {sensorData.map((sensor, index) => (
                  <div
                    key={index}
                    className="bg-white rounded-xl shadow-sm p-6 border-l-4 border-green-500"
                  >
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-2">
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          width="20"
                          height="20"
                          viewBox="0 0 24 24"
                          fill="none"
                          stroke="#10B981"
                          strokeWidth="2"
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          className="lucide lucide-check-circle"
                        >
                          <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14" />
                          <polyline points="22 4 12 14.01 9 11.01" />
                        </svg>
                        <span className="text-lg font-semibold text-gray-800">{sensor.label}</span>
                      </div>
                    </div>
                    {/* Display all key-value pairs from the sensor object dynamically */}
                    <div className="space-y-4">
                      {Object.keys(sensor).filter(key => key !== 'label' && key !== 'deviceId' && key !== 'id' && key !== 'lastUpdated' && key !== 'type').map(key => (
                        <div key={key} className="flex items-center justify-between">
                          <p className="text-gray-500">{key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}</p>
                          <span className="font-bold text-gray-900">{typeof sensor[key] === 'object' && sensor[key] !== null ? `${sensor[key].value}` : sensor[key]}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </section>
          </>
        )}

        {/* Placeholder Section (from original code) */}
        <div className="bg-white rounded-xl shadow-sm p-6 sm:p-8 text-center mt-8">
          <h3 className="text-xl font-semibold text-gray-800">Detailed Reports and Analytics</h3>
          <p className="text-gray-500 mt-2">Navigate to the reports section to view historical data and detailed graphs.</p>
        </div>
      </div>
    </div>
  );
};

export default App;
