import React, { useEffect, useState } from "react";
import { Thermometer, Droplets, Activity, Server, AlertCircle } from "lucide-react";
import axios from "axios";

const WelcomePage = () => {
  const [data, setData] = useState({
    totalDevices: 0,
    activeSensors: 0,
    avgTemperature: null,
    avgHumidity: null,
    activeAlerts: 0,
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const devicesRes = await axios.get("https://sensorium-api.vercel.app/api/devices");
        const devices = devicesRes.data;
        const totalDevices = devices.length;

        let activeSensors = 0;
        const sensorPromises = devices.map((device) =>
          axios.get(`https://sensorium-api.vercel.app/api/sensors/${device.deviceId}`)
        );

        const sensorResponses = await Promise.all(sensorPromises);
        sensorResponses.forEach((res) => {
          activeSensors += res.data.length;
        });

        // Mock data (replace with backend values later)
        const mockAvgTemperature = 22.5;
        const mockAvgHumidity = 65;
        const mockActiveAlerts = 2;

        setData({
          totalDevices,
          activeSensors,
          avgTemperature: mockAvgTemperature,
          avgHumidity: mockAvgHumidity,
          activeAlerts: mockActiveAlerts,
        });
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const statCards = [
    {
      title: "Total Devices",
      value: data.totalDevices,
      icon: <Server size={36} className="text-blue-500" />,
      color: "border-blue-500 bg-gradient-to-r from-blue-50 to-white",
    },
    {
      title: "Active Sensors",
      value: data.activeSensors,
      icon: <Activity size={36} className="text-green-500" />,
      color: "border-green-500 bg-gradient-to-r from-green-50 to-white",
    },
    {
      title: "Avg. Temperature",
      value: data.avgTemperature !== null ? `${data.avgTemperature}Â°C` : "N/A",
      icon: <Thermometer size={36} className="text-red-500" />,
      color: "border-red-500 bg-gradient-to-r from-red-50 to-white",
    },
    {
      title: "Avg. Humidity",
      value: data.avgHumidity !== null ? `${data.avgHumidity}%` : "N/A",
      icon: <Droplets size={36} className="text-purple-500" />,
      color: "border-purple-500 bg-gradient-to-r from-purple-50 to-white",
    },
    {
      title: "Active Alerts",
      value: data.activeAlerts,
      icon: <AlertCircle size={36} className="text-yellow-500" />,
      color: "border-yellow-500 bg-gradient-to-r from-yellow-50 to-white",
    },
  ];

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="mb-10">
        <h1 className="text-4xl font-extrabold text-gray-800 tracking-tight">
          Welcome to <span className="text-blue-600">Sensorium</span>
        </h1>
        <p className="mt-2 text-gray-600 text-lg">
          Your centralized hub for <span className="font-semibold">real-time sensor monitoring</span> and analytics.
        </p>
      </div>

      {/* Overview */}
      <h2 className="text-2xl font-semibold text-gray-700 mb-6 flex items-center">
        <span className="w-1.5 h-6 bg-blue-600 mr-3 rounded"></span>
        System Overview
      </h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-6">
        {statCards.map((card, index) => (
          <div
            key={index}
            className={`flex flex-col items-center justify-center rounded-xl border-2 shadow-md p-6 text-center transition transform hover:-translate-y-1 hover:shadow-lg ${card.color}`}
          >
            <div className="mb-3">{card.icon}</div>
            <p className="text-sm font-medium text-gray-500">{card.title}</p>
            <h3 className="text-2xl font-bold text-gray-800 mt-1">{card.value}</h3>
          </div>
        ))}
      </div>

      {/* Placeholder Section */}
      <div className="mt-12 bg-white shadow-md border border-gray-200 rounded-xl p-8 text-center">
        <h3 className="text-xl font-semibold text-gray-800 mb-2">
          ðŸ“Š Detailed Reports & Analytics
        </h3>
        <p className="text-gray-500 max-w-2xl mx-auto">
          Navigate to the <span className="font-medium text-blue-600">Reports</span> section to explore
          historical trends, performance insights, and detailed visualizations for better decision-making.
        </p>
      </div>
    </div>
  );
};

export default WelcomePage;
