import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import {
  Thermometer,
  Droplets,
  Activity,
  Cloud,
  Server,
  AlertCircle,
  Cpu,
  Calendar,
  LayoutDashboard,
  BarChart,
  RefreshCcw,
} from 'lucide-react';

const App = () => {
  const [currentPage, setCurrentPage] = useState("welcome");
  const [selectedReport, setSelectedReport] = useState(null);
  const [loading, setLoading] = useState(true);

  // A basic loading screen component
  const SensorLoadingScreen = () => {
    return (
      <div className="loading-container">
        <div className="spinner">
          <RefreshCcw className="spinner-icon" />
        </div>
        <p className="loading-text">Loading Sensorium...</p>
      </div>
    );
  };

  // Welcome Page component
  const WelcomePage = () => {
    const [data, setData] = useState({
      totalDevices: 0,
      activeSensors: 0,
      avgTemperature: null,
      avgHumidity: null,
      activeAlerts: 0,
    });

    useEffect(() => {
      const fetchData = async () => {
        try {
          const devicesRes = await axios.get("https://sensorium-api.vercel.app/api/devices");
          const devices = devicesRes.data;
          const totalDevices = devices.length;

          let activeSensors = 0;
          const sensorPromises = devices.map(device =>
            axios.get(`https://sensorium-api.vercel.app/api/sensors/${device.deviceId}`)
          );

          const sensorResponses = await Promise.all(sensorPromises);
          sensorResponses.forEach(res => {
            activeSensors += res.data.length;
          });

          // Mocking the averages and alerts for this demonstration
          const mockAvgTemperature = 22.5;
          const mockAvgHumidity = 65;
          const mockActiveAlerts = 2;

          setData({
            totalDevices,
            activeSensors,
            avgTemperature: mockAvgTemperature,
            avgHumidity: mockAvgHumidity,
            activeAlerts: mockActiveAlerts,
          });
        } catch (error) {
          console.error("Error fetching data:", error);
        }
      };
      fetchData();
    }, []);

    const statCards = [
      {
        title: 'Total Devices',
        value: data.totalDevices,
        icon: <Server className="w-8 h-8 text-blue-500" />,
        color: 'bg-blue-100',
      },
      {
        title: 'Active Sensors',
        value: data.activeSensors,
        icon: <Activity className="w-8 h-8 text-green-500" />,
        color: 'bg-green-100',
      },
      {
        title: 'Avg. Temperature',
        value: data.avgTemperature !== null ? `${data.avgTemperature}Â°C` : 'N/A',
        icon: <Thermometer className="w-8 h-8 text-red-500" />,
        color: 'bg-red-100',
      },
      {
        title: 'Avg. Humidity',
        value: data.avgHumidity !== null ? `${data.avgHumidity}%` : 'N/A',
        icon: <Droplets className="w-8 h-8 text-indigo-500" />,
        color: 'bg-indigo-100',
      },
      {
        title: 'Active Alerts',
        value: data.activeAlerts,
        icon: <AlertCircle className="w-8 h-8 text-yellow-500" />,
        color: 'bg-yellow-100',
      },
    ];

    return (
      <div className="p-4 sm:p-6 md:p-8 bg-gray-50 min-h-screen">
        <div className="max-w-7xl mx-auto">
          <div className="bg-white rounded-2xl shadow-lg p-6 sm:p-8 flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4 mb-8">
            <div>
              <h1 className="text-3xl sm:text-4xl font-extrabold text-gray-800">
                Welcome to Sensorium
              </h1>
              <p className="text-sm sm:text-base text-gray-600 mt-1">
                Your centralized hub for real-time sensor monitoring.
              </p>
            </div>
          </div>
          <h2 className="text-2xl font-bold text-gray-800 mb-6">
            System Overview
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-6">
            {statCards.map((card, index) => (
              <div
                key={index}
                className="bg-white rounded-2xl shadow-md p-6 flex flex-col items-start transition-transform duration-300 hover:scale-105 hover:shadow-xl"
              >
                <div
                  className={`w-14 h-14 rounded-full flex items-center justify-center ${card.color}`}
                >
                  {card.icon}
                </div>
                <p className="text-sm text-gray-500 font-medium mt-4">
                  {card.title}
                </p>
                <h3 className="text-3xl font-extrabold text-gray-900 mt-1">
                  {card.value}
                </h3>
              </div>
            ))}
          </div>
          <div className="mt-12">
            <div className="bg-white rounded-2xl shadow-lg p-8 text-center text-gray-500">
              <h3 className="text-xl font-semibold mb-2">Detailed Reports and Analytics</h3>
              <p>
                Navigate to the reports section to view historical data and detailed graphs.
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Main Content Component
  const MainContent = ({ sensorType }) => {
    return (
      <div className="p-8 bg-white rounded-2xl shadow-lg">
        <h1 className="text-3xl font-bold mb-4 flex items-center gap-2">
          <Server className="w-8 h-8 text-blue-600" />
          {sensorType}
        </h1>
        <p className="text-gray-600">
          This is a placeholder for the content of the {sensorType} page. Here you would display a list of devices, real-time data, and controls.
        </p>
      </div>
    );
  };

  // Reports Page Component
  const ReportsPage = ({ period }) => {
    const [devices, setDevices] = useState([]);
    const [sensors, setSensors] = useState([]);
    const [selectedDevice, setSelectedDevice] = useState("");
    const [selectedSensor, setSelectedSensor] = useState("");
    const [reportData, setReportData] = useState([]);

    useEffect(() => {
      axios.get("https://sensorium-api.vercel.app/api/devices").then((res) => {
        setDevices(res.data);
      });
    }, []);

    useEffect(() => {
      if (selectedDevice) {
        axios
          .get(`https://sensorium-api.vercel.app/api/sensors/${selectedDevice}`)
          .then((res) => {
            setSensors(res.data);
            setSelectedSensor("");
          });
      } else {
        setSensors([]);
        setSelectedSensor("");
      }
    }, [selectedDevice]);

    const periodMap = {
      "Daily Report": "daily",
      "Weekly Report": "weekly",
      "Monthly Report": "monthly",
    };

    useEffect(() => {
      if (selectedDevice && selectedSensor && period) {
        const backendPeriod = periodMap[period];
        if (!backendPeriod) {
          setReportData([]);
          return;
        }
        axios
          .get(
            `https://sensorium-api.vercel.app/api/reports?deviceId=${selectedDevice}&sensorId=${selectedSensor}&period=${backendPeriod}`
          )
          .then((res) => {
            setReportData(res.data);
          })
          .catch((error) => {
            console.error("Error fetching report:", error);
            setReportData([]);
          });
      }
    }, [selectedDevice, selectedSensor, period]);

    const formatPeriodLabel = (date) => {
      const d = new Date(date);
      if (period === "Daily Report") {
        return d.toLocaleString([], {
          year: 'numeric',
          month: '2-digit',
          day: '2-digit',
          hour: '2-digit',
          minute: '2-digit',
        });
      }
      return d.toLocaleDateString();
    };

    return (
      <div className="p-4 sm:p-6 md:p-8 bg-gray-50 min-h-screen">
        <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-2">
          <Calendar className="w-6 h-6 text-blue-600" />
          Reports - {period}
        </h2>
        <div className="bg-white rounded-2xl shadow-lg p-6 flex flex-col sm:flex-row items-center justify-start gap-4 mb-8">
          <div className="flex-1 min-w-0">
            <label className="block text-gray-700 font-medium mb-1">Select Device</label>
            <div className="flex items-center space-x-2">
              <Server className="text-gray-400" size={20} />
              <select
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-shadow"
                value={selectedDevice}
                onChange={(e) => setSelectedDevice(e.target.value)}
              >
                <option value="">Select a device</option>
                {devices.map((d) => (
                  <option key={d.deviceId} value={d.deviceId}>
                    {d.deviceName}
                  </option>
                ))}
              </select>
            </div>
          </div>
          <div className="flex-1 min-w-0">
            <label className="block text-gray-700 font-medium mb-1">Select Sensor</label>
            <div className="flex items-center space-x-2">
              <Cpu className="text-gray-400" size={20} />
              <select
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-shadow"
                value={selectedSensor}
                onChange={(e) => setSelectedSensor(e.target.value)}
                disabled={!selectedDevice}
              >
                <option value="">Select a sensor</option>
                {sensors.map((s) => (
                  <option key={s.sensorId} value={s.sensorId}>
                    {s.label || `Sensor ${s.sensorId}`}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
        {reportData.length > 0 ? (
          <div className="bg-white rounded-2xl shadow-lg p-6 mb-8">
            <ResponsiveContainer width="100%" height={400}>
              <LineChart data={reportData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="period" tickFormatter={formatPeriodLabel} />
                <YAxis />
                <Tooltip labelFormatter={formatPeriodLabel} />
                <Legend />
                <Line
                  type="monotone"
                  dataKey="avgTemp"
                  stroke="#007bff"
                  name="Avg Temp"
                />
                <Line
                  type="monotone"
                  dataKey="avgHumidity"
                  stroke="#28a745"
                  name="Avg Humidity"
                />
                <Line
                  type="monotone"
                  dataKey="avgHeatIndex"
                  stroke="#ff5722"
                  name="Avg Heat Index"
                />
                <Line
                  type="monotone"
                  dataKey="avgDewPoint"
                  stroke="#6f42c1"
                  name="Avg Dew Point"
                />
              </LineChart>
            </ResponsiveContainer>
            <div className="mt-6 overflow-x-auto">
              <h3 className="text-lg font-semibold mb-4">Detailed Data</h3>
              <table className="min-w-full bg-white border border-gray-200 rounded-lg">
                <thead>
                  <tr className="bg-gray-100 text-gray-600 uppercase text-sm leading-normal">
                    <th className="py-3 px-6 text-left">Period</th>
                    <th className="py-3 px-6 text-left">Avg Temp (Â°C)</th>
                    <th className="py-3 px-6 text-left">Avg Humidity (%)</th>
                    <th className="py-3 px-6 text-left">Avg Heat Index (Â°C)</th>
                    <th className="py-3 px-6 text-left">Avg Dew Point (Â°C)</th>
                  </tr>
                </thead>
                <tbody className="text-gray-600 text-sm font-light">
                  {reportData.map((row, index) => (
                    <tr key={index} className="border-b border-gray-200 hover:bg-gray-50">
                      <td className="py-3 px-6 text-left whitespace-nowrap">{formatPeriodLabel(row.period)}</td>
                      <td className="py-3 px-6 text-left">{row.avgTemp !== null ? row.avgTemp.toFixed(2) : "-"}</td>
                      <td className="py-3 px-6 text-left">{row.avgHumidity !== null ? row.avgHumidity.toFixed(2) : "-"}</td>
                      <td className="py-3 px-6 text-left">{row.avgHeatIndex !== null ? row.avgHeatIndex.toFixed(2) : "-"}</td>
                      <td className="py-3 px-6 text-left">{row.avgDewPoint !== null ? row.avgDewPoint.toFixed(2) : "-"}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        ) : (
          <div className="bg-white rounded-2xl shadow-lg p-8 text-center text-gray-500">
            <p>Select a device and sensor to view the report.</p>
          </div>
        )}
      </div>
    );
  };

  // Sidebar component
  const Sidebar = ({ onPageSelect, onReportSelect, currentPage, selectedReport }) => {
    return (
      <aside className="sidebar">
        <h2 className="logo">Sensorium</h2>
        <nav className="nav-menu">
          <button
            onClick={() => onPageSelect("welcome")}
            className={`nav-button ${currentPage === "welcome" ? "active" : ""}`}
          >
            <LayoutDashboard className="icon" />
            <span>Dashboard</span>
          </button>
          <button
            onClick={() => onPageSelect("digital-sensors")}
            className={`nav-button ${currentPage === "digital-sensors" ? "active" : ""}`}
          >
            <Server className="icon" />
            <span>Digital Sensors</span>
          </button>
          <div className="report-group">
            <h3 className="report-title">Reports</h3>
            <button
              onClick={() => onReportSelect("Daily Report")}
              className={`nav-button ${selectedReport === "Daily Report" ? "active" : ""}`}
            >
              <BarChart className="icon" />
              <span>Daily Report</span>
            </button>
            <button
              onClick={() => onReportSelect("Weekly Report")}
              className={`nav-button ${selectedReport === "Weekly Report" ? "active" : ""}`}
            >
              <Calendar className="icon" />
              <span>Weekly Report</span>
            </button>
            <button
              onClick={() => onReportSelect("Monthly Report")}
              className={`nav-button ${selectedReport === "Monthly Report" ? "active" : ""}`}
            >
              <Calendar className="icon" />
              <span>Monthly Report</span>
            </button>
          </div>
        </nav>
      </aside>
    );
  };

  // Main application logic
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 2000);
    return () => clearTimeout(timer);
  }, []);

  const handlePageSelect = (pageType) => {
    setCurrentPage(pageType);
    setSelectedReport(null);
  };

  const handleReportSelect = (reportType) => {
    setCurrentPage("reports");
    setSelectedReport(reportType);
  };

  const renderContent = () => {
    switch (currentPage) {
      case "welcome":
        return <WelcomePage />;
      case "digital-sensors":
        return <MainContent sensorType="Digital Sensors" />;
      case "reports":
        return <ReportsPage period={selectedReport} />;
      default:
        return <WelcomePage />;
    }
  };

  if (loading) {
    return <SensorLoadingScreen />;
  }

  return (
    <div className="app-container">
      <Sidebar
        onPageSelect={handlePageSelect}
        onReportSelect={handleReportSelect}
        currentPage={currentPage}
        selectedReport={selectedReport}
      />
      <main className="main-content">{renderContent()}</main>
      <style>
        {`
        .app-container {
          display: flex;
          min-height: 100vh;
          background-color: #f3f4f6;
          font-family: "Inter", sans-serif;
        }

        .main-content {
          flex: 1;
          padding: 1.5rem;
          margin-left: 200px;
        }

        .sidebar {
          width: 200px;
          background-color: #ffffff;
          padding: 1.5rem 1rem;
          box-shadow: 2px 0 5px rgba(0, 0, 0, 0.05);
          position: fixed;
          top: 0;
          left: 0;
          height: 100%;
          display: flex;
          flex-direction: column;
        }

        .logo {
          font-size: 1.5rem;
          font-weight: 700;
          color: #1f2937;
          margin-bottom: 2rem;
          text-align: center;
        }

        .nav-menu {
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
        }

        .nav-button {
          display: flex;
          align-items: center;
          gap: 0.75rem;
          padding: 0.75rem 1rem;
          border-radius: 0.75rem;
          background-color: transparent;
          color: #4b5563;
          font-weight: 500;
          cursor: pointer;
          transition: all 0.2s ease-in-out;
          border: none;
          width: 100%;
          text-align: left;
        }

        .nav-button:hover {
          background-color: #f3f4f6;
          color: #1f2937;
        }

        .nav-button.active {
          background-color: #2563eb;
          color: #ffffff;
          box-shadow: 0 4px 6px rgba(37, 99, 235, 0.2);
        }

        .nav-button.active .icon {
          color: #ffffff;
        }

        .nav-button .icon {
          width: 1.25rem;
          height: 1.25rem;
          color: #6b7280;
          transition: color 0.2s ease-in-out;
        }

        .report-group {
          margin-top: 1rem;
          padding-top: 1rem;
          border-top: 1px solid #e5e7eb;
        }

        .report-title {
          font-size: 0.875rem;
          font-weight: 600;
          color: #9ca3af;
          text-transform: uppercase;
          margin-bottom: 0.5rem;
          padding-left: 1rem;
        }
        .loading-container {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          height: 100vh;
          background-color: #f3f4f6;
          color: #4b5563;
        }

        .spinner {
          animation: spin 1s linear infinite;
        }

        .spinner-icon {
          width: 4rem;
          height: 4rem;
          color: #2563eb;
        }

        .loading-text {
          margin-top: 1rem;
          font-size: 1.125rem;
          font-weight: 500;
        }

        @keyframes spin {
          from {
            transform: rotate(0deg);
          }
          to {
            transform: rotate(360deg);
          }
        }
        `}
      </style>
    </div>
  );
};

export default App;
