import React from "react";

const SensorLoadingScreen = () => {
  return (
    <div className="fixed inset-0 flex items-center justify-center bg-gray-950 bg-opacity-80 backdrop-blur-sm z-50 animate-fadeIn">
      <div className="flex flex-col items-center space-y-6">
        {/* Spinner with Halo */}
        <div className="relative w-16 h-16">
          {/* Halo Glow */}
          <div className="absolute inset-0 rounded-full bg-blue-500 opacity-30 blur-xl animate-pulse"></div>

          {/* Base Ring */}
          <div className="absolute inset-0 rounded-full border-4 border-gray-700/30"></div>

          {/* Spinning Ring */}
          <div className="absolute inset-0 rounded-full border-4 border-blue-500 border-t-transparent animate-spin"></div>
        </div>

        {/* Elegant Loading Text */}
        <div className="flex items-center text-lg font-medium text-gray-100 tracking-wide">
          <span className="mr-2">Connecting to Sensors</span>
          <span className="flex space-x-1">
            <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-bounce"></span>
            <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-bounce delay-150"></span>
            <span className="w-1.5 h-1.5 bg-blue-400 rounded-full animate-bounce delay-300"></span>
          </span>
        </div>
      </div>

      {/* Custom keyframes */}
      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; }
            to { opacity: 1; }
          }
          .animate-fadeIn {
            animation: fadeIn 0.6s ease-in-out;
          }
        `}
      </style>
    </div>
  );
};

export default SensorLoadingScreen;
