import React, { useEffect, useState } from "react";
import {
  Thermometer,
  Droplets,
  Activity,
  Server,
  AlertCircle,
} from "lucide-react";
import axios from "axios";

const WelcomePage = () => {
  const [data, setData] = useState({
    totalDevices: 0,
    activeSensors: 0,
    avgTemperature: null,
    avgHumidity: null,
    activeAlerts: 0,
  });
  const [lastSync, setLastSync] = useState(new Date());

  const fetchData = async () => {
    try {
      const devicesRes = await axios.get(
        "https://sensorium-api.vercel.app/api/devices"
      );
      const devices = devicesRes.data;
      const totalDevices = devices.length;

      let activeSensors = 0;
      const sensorPromises = devices.map((device) =>
        axios.get(
          `https://sensorium-api.vercel.app/api/sensors/${device.deviceId}`
        )
      );

      const sensorResponses = await Promise.all(sensorPromises);
      sensorResponses.forEach((res) => {
        activeSensors += res.data.length;
      });

      const mockAvgTemperature = 22.5;
      const mockAvgHumidity = 65;
      const mockActiveAlerts = 2;

      setData({
        totalDevices,
        activeSensors,
        avgTemperature: mockAvgTemperature,
        avgHumidity: mockAvgHumidity,
        activeAlerts: mockActiveAlerts,
      });
      setLastSync(new Date());
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchData(); // initial fetch

    const interval = setInterval(() => {
      fetchData();
      setLastSync(new Date());
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const statCards = [
    {
      title: "Total Devices",
      value: data.totalDevices,
      icon: <Server size={28} className="text-blue-500" />,
      color: "from-blue-50 to-blue-25",
      iconBg: "bg-blue-100",
    },
    {
      title: "Active Sensors",
      value: data.activeSensors,
      icon: <Activity size={28} className="text-green-500" />,
      color: "from-green-50 to-green-25",
      iconBg: "bg-green-100",
    },
    {
      title: "Avg. Temperature",
      value: data.avgTemperature !== null ? `${data.avgTemperature}Â°C` : "N/A",
      icon: <Thermometer size={28} className="text-red-500" />,
      color: "from-red-50 to-red-25",
      iconBg: "bg-red-100",
    },
    {
      title: "Avg. Humidity",
      value: data.avgHumidity !== null ? `${data.avgHumidity}%` : "N/A",
      icon: <Droplets size={28} className="text-purple-500" />,
      color: "from-purple-50 to-purple-25",
      iconBg: "bg-purple-100",
    },
    {
      title: "Active Alerts",
      value: data.activeAlerts,
      icon: <AlertCircle size={28} className="text-yellow-500" />,
      color: "from-yellow-50 to-yellow-25",
      iconBg: "bg-yellow-100",
    },
  ];

  // Tailwind-compatible blue shades for background dots
  const dotColors = [
    "bg-blue-300",
    "bg-blue-400",
    "bg-blue-500",
    "bg-blue-200",
    "bg-blue-100",
  ];

  return (
    <div className="relative min-h-screen p-8 bg-gradient-to-br from-white via-gray-50 to-white overflow-hidden">
      {/* Animated Background Dots */}
      <div className="absolute inset-0 -z-10">
        {[...Array(40)].map((_, i) => {
          const size = Math.random() * 3 + 2; // 2px to 5px
          const top = Math.random() * 100;
          const left = Math.random() * 100;
          const delay = Math.random() * 5;
          const duration = Math.random() * 5 + 5;
          const colorClass = dotColors[Math.floor(Math.random() * dotColors.length)];
          const opacity = (Math.random() * 0.4 + 0.1).toFixed(2); // 0.1 to 0.5

          return (
            <div
              key={i}
              className={`absolute rounded-full animate-float ${colorClass}`}
              style={{
                width: `${size}px`,
                height: `${size}px`,
                top: `${top}%`,
                left: `${left}%`,
                animationDelay: `${delay}s`,
                animationDuration: `${duration}s`,
                opacity: opacity,
              }}
            />
          );
        })}
      </div>

      {/* Header */}
      <div className="mb-12 flex flex-col lg:flex-row lg:items-center lg:justify-between gap-6">
        <div>
         <h1 className="relative text-4xl lg:text-5xl font-extrabold tracking-tight flex flex-wrap overflow-hidden">
  {/* Moving gradient background */}
  <span className="absolute inset-0 bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 animate-gradient-x opacity-30"></span>
  
  <span className="relative inline-block opacity-0 animate-fadeUp text-gray-500">
    Welcome
  </span>
  <span className="relative inline-block opacity-0 animate-fadeUpDelay200 ml-3 text-gray-500">
    to
  </span>
  <span className="relative inline-block opacity-0 animate-fadeUpDelay400 ml-3 text-blue-400 drop-shadow-sm">
    Sensorium
  </span>
</h1>

   <p className="relative mt-2 text-gray-600 text-lg lg:text-xl overflow-hidden">
      <span className="absolute inset-0 bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 animate-gradient-x opacity-20"></span>
      <span className="relative">
        Your centralized hub for <span className="font-semibold">real-time monitoring</span> and intelligent insights.
      </span>
    </p>
        </div>
      </div>

      {/* Overview Section */}
      <h2 className="text-2xl font-semibold text-gray-700 mb-6 flex items-center">
        <span className="w-1.5 h-6 bg-blue-600 mr-3 rounded-full"></span>
        System Overview
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-6">
        {statCards.map((card, index) => (
          <div
            key={index}
            className={`flex flex-col items-center justify-center rounded-xl border shadow-lg p-6 text-center transition-transform duration-300 hover:-translate-y-2 hover:shadow-2xl bg-gradient-to-br ${card.color}`}
          >
            <div
              className={`mb-4 p-4 rounded-full shadow-md flex items-center justify-center animate-pulseSlow ${card.iconBg}`}
            >
              {card.icon}
            </div>
            <p className="text-sm font-medium text-gray-500">{card.title}</p>
            <h3 className="text-2xl font-bold text-gray-800 mt-1">{card.value}</h3>
          </div>
        ))}
      </div>

      {/* Reports Section */}
      <div className="mt-16 bg-white shadow-xl border border-gray-200 rounded-xl p-10 text-center hover:shadow-2xl transition-shadow">
        <h3 className="text-2xl lg:text-3xl font-bold text-gray-800 mb-4">
          ð Reports & Analytics
        </h3>
        <p className="text-gray-600 max-w-2xl mx-auto leading-relaxed text-lg">
          Navigate to the reports section to view historical data and detailed graphs
        </p>
      </div>
    </div>
  );
};

export default WelcomePage;
