import React, { useState } from 'react';

const Sidebar = ({ activeItem, setActiveItem }) => {
  const menuItems = [
    'Dashboard',
    'Digital Sensors',
    'Analog Sensors',
    'Switch Sensors',
    'Internal Relays',
    'External Relays',
    'Power Sensors',
  ];

  return (
    <aside className="w-64 bg-white border-r border-gray-200 h-screen p-4 flex flex-col">
      <div className="flex-shrink-0 mb-8">
        <h1 className="text-2xl font-bold text-blue-600">Dashboard</h1>
      </div>
      <nav className="flex-grow">
        <ul className="space-y-2">
          {menuItems.map(item => (
            <li key={item}>
              <button
                className={`w-full text-left py-2 px-4 rounded-lg transition-colors duration-200 ease-in-out ${
                  activeItem === item
                    ? 'bg-blue-100 text-blue-700 font-semibold'
                    : 'text-gray-600 hover:bg-gray-100'
                }`}
                onClick={() => setActiveItem(item)}
              >
                {item}
              </button>
            </li>
          ))}
          {/* Reports dropdown section below all other options */}
          <li>
            <button
              className={`w-full text-left py-2 px-4 rounded-lg transition-colors duration-200 ease-in-out text-gray-600 hover:bg-gray-100`}
              onClick={() => setActiveItem('Reports')}
            >
              Reports â–¼
            </button>
          </li>
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
