import React, { useEffect, useState } from "react";
import { Thermometer, Droplets, Activity, Server, AlertCircle } from "lucide-react";
import axios from "axios";

const WelcomePage = () => {
  const [data, setData] = useState({
    totalDevices: 0,
    activeSensors: 0,
    avgTemperature: null,
    avgHumidity: null,
    activeAlerts: 0,
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const devicesRes = await axios.get("https://sensorium-api.vercel.app/api/devices");
        const devices = devicesRes.data;
        const totalDevices = devices.length;

        let activeSensors = 0;
        const sensorPromises = devices.map((device) =>
          axios.get(`https://sensorium-api.vercel.app/api/sensors/${device.deviceId}`)
        );

        const sensorResponses = await Promise.all(sensorPromises);
        sensorResponses.forEach((res) => {
          activeSensors += res.data.length;
        });

        // Mock data for now
        const mockAvgTemperature = 22.5;
        const mockAvgHumidity = 65;
        const mockActiveAlerts = 2;

        setData({
          totalDevices,
          activeSensors,
          avgTemperature: mockAvgTemperature,
          avgHumidity: mockAvgHumidity,
          activeAlerts: mockActiveAlerts,
        });
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const statCards = [
    {
      title: "Total Devices",
      value: data.totalDevices,
      icon: <Server size={32} className="text-blue-500" />,
      color: "border-blue-500 hover:bg-blue-50",
    },
    {
      title: "Active Sensors",
      value: data.activeSensors,
      icon: <Activity size={32} className="text-green-500" />,
      color: "border-green-500 hover:bg-green-50",
    },
    {
      title: "Avg. Temperature",
      value: data.avgTemperature !== null ? `${data.avgTemperature}°C` : "N/A",
      icon: <Thermometer size={32} className="text-red-500" />,
      color: "border-red-500 hover:bg-red-50",
    },
    {
      title: "Avg. Humidity",
      value: data.avgHumidity !== null ? `${data.avgHumidity}%` : "N/A",
      icon: <Droplets size={32} className="text-purple-500" />,
      color: "border-purple-500 hover:bg-purple-50",
    },
    {
      title: "Active Alerts",
      value: data.activeAlerts,
      icon: <AlertCircle size={32} className="text-yellow-500" />,
      color: "border-yellow-500 hover:bg-yellow-50",
    },
  ];

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-800">Welcome to Sensorium</h1>
        <p className="text-gray-600">Your centralized hub for real-time sensor monitoring.</p>
      </div>

      {/* Overview */}
      <h2 className="text-xl font-semibold text-gray-700 mb-4">System Overview</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {statCards.map((card, index) => (
          <div
            key={index}
            className={`flex flex-col items-center justify-center bg-white shadow-md rounded-lg border-2 p-6 transition ${card.color}`}
          >
            <div className="mb-2">{card.icon}</div>
            <p className="text-sm text-gray-600">{card.title}</p>
            <h3 className="text-2xl font-bold text-gray-800">{card.value}</h3>
          </div>
        ))}
      </div>

      {/* Placeholder Section */}
      <div className="mt-10 bg-white shadow-sm border border-gray-200 rounded-lg p-6 text-center">
        <h3 className="text-lg font-semibold text-gray-700">Detailed Reports and Analytics</h3>
        <p className="text-gray-500">
          Navigate to the reports section to view historical data and detailed graphs.
        </p>
      </div>
    </div>
  );
};

export default WelcomePage;
