import React, { useState, useEffect } from 'react';
import AllSensorsDisplay from './AllSensorsDisplay';

const API_BASE_URL = 'http://localhost:5000/api';

const MainContent = () => {
  const [allSensorsData, setAllSensorsData] = useState([]);
  const [deviceInfo, setDeviceInfo] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchAllData() {
      setLoading(true);
      try {
        const [sensorsResponse, devicesResponse] = await Promise.all([
          fetch(`${API_BASE_URL}/live/digital-sensors`),
          fetch(`${API_BASE_URL}/live/devices`),
        ]);

        if (!sensorsResponse.ok || !devicesResponse.ok) {
          throw new Error('Failed to fetch data');
        }

        const sensorsData = await sensorsResponse.json();
        const devicesData = await devicesResponse.json();

        setAllSensorsData(sensorsData);
        setDeviceInfo(devicesData);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    }
    fetchAllData();
  }, []);

  if (loading) {
    return (
      <main className="main-content">
        <div className="content-area">
          <div className="loading-state-card">Loading live data...</div>
        </div>
      </main>
    );
  }

  return (
    <main className="main-content">
      <header className="main-header">
        <h2 className="header-title">Live Sensor Data</h2>
        <span className="last-updated">Last updated: {new Date().toLocaleTimeString()}</span>
      </header>
      <div className="content-area">
        <AllSensorsDisplay allSensorsData={allSensorsData} deviceInfo={deviceInfo} />
      </div>
    </main>
  );
};

export default MainContent;
