import React, { useState } from 'react';
import Sidebar from './components0/Sidebar';
import MainContent from './MainContent'; // This will be your new component
import './App.css'; // For the main layout

const App = () => {
  const [selectedSensorType, setSelectedSensorType] = useState('Digital Sensors');

  const handleSensorSelect = (sensorType) => {
    setSelectedSensorType(sensorType);
  };

  return (
    <div className="app-container">
      <Sidebar 
        onSensorSelect={handleSensorSelect} 
        activeSensorType={selectedSensorType} 
      />
      <main className="main-content">
        <MainContent sensorType={selectedSensorType} />
      </main>
    </div>
  );
};

export default App;
