import React from "react";
import {
  LayoutDashboard,
  BarChart2,
  Thermometer,
  Droplets,
  Power,
  ToggleLeft,
} from "lucide-react";

const Sidebar = ({
  onSensorSelect,
  onReportSelect,
  onWelcomeSelect,
  activeSensorType,
  selectedPage,
  selectedReport,
  reportsOpen,
  setReportsOpen,
}) => {
  const sensors = [
    { label: "Temperature & Humidity", icon: <Thermometer size={20} /> },
    { label: "Power Sensors", icon: <Power size={20} /> },
    { label: "Switch Sensors", icon: <ToggleLeft size={20} /> },
  ];

  const reports = ["Daily", "Weekly", "Monthly"];

  return (
    <aside className="w-64 bg-white shadow-md border-r border-gray-200 flex flex-col">
      {/* Logo / Header */}
      <div className="px-6 py-5 border-b border-gray-200">
        <h1 className="text-xl font-bold text-blue-600 flex items-center gap-2">
          <LayoutDashboard size={22} />
          Sensorium
        </h1>
      </div>

      {/* Navigation */}
      <nav className="flex-1 overflow-y-auto px-4 py-6 space-y-6">
        {/* Welcome */}
        <button
          onClick={onWelcomeSelect}
          className={`flex items-center w-full px-3 py-2 rounded-lg text-sm font-medium transition ${
            selectedPage === "welcome"
              ? "bg-blue-50 text-blue-600"
              : "text-gray-600 hover:bg-gray-100"
          }`}
        >
          <LayoutDashboard size={18} className="mr-2" />
          Dashboard
        </button>

        {/* Sensors */}
        <div>
          <p className="text-xs font-semibold text-gray-500 uppercase mb-2">
            Sensors
          </p>
          <div className="space-y-1">
            {sensors.map((sensor) => (
              <button
                key={sensor.label}
                onClick={() => onSensorSelect(sensor.label)}
                className={`flex items-center w-full px-3 py-2 rounded-lg text-sm transition ${
                  activeSensorType === sensor.label
                    ? "bg-blue-50 text-blue-600"
                    : "text-gray-600 hover:bg-gray-100"
                }`}
              >
                {sensor.icon}
                <span className="ml-2">{sensor.label}</span>
              </button>
            ))}
          </div>
        </div>

        {/* Reports */}
        <div>
          <button
            onClick={() => setReportsOpen(!reportsOpen)}
            className={`flex items-center justify-between w-full px-3 py-2 rounded-lg text-sm font-medium transition ${
              selectedPage === "reports"
                ? "bg-blue-50 text-blue-600"
                : "text-gray-600 hover:bg-gray-100"
            }`}
          >
            <span className="flex items-center">
              <BarChart2 size={18} className="mr-2" />
              Reports
            </span>
            <span className="text-xs">{reportsOpen ? "▲" : "▼"}</span>
          </button>

          {reportsOpen && (
            <div className="ml-4 mt-2 space-y-1">
              {reports.map((report) => (
                <button
                  key={report}
                  onClick={() => onReportSelect(report)}
                  className={`w-full text-left px-3 py-1.5 rounded-md text-sm transition ${
                    selectedReport === report
                      ? "bg-blue-50 text-blue-600"
                      : "text-gray-600 hover:bg-gray-100"
                  }`}
                >
                  {report}
                </button>
              ))}
            </div>
          )}
        </div>
      </nav>

      {/* Footer */}
      <div className="px-6 py-4 border-t border-gray-200 text-xs text-gray-500">
        © {new Date().getFullYear()} Sensorium
      </div>
    </aside>
  );
};

export default Sidebar;
