import React, { useState, useEffect } from 'react';
import { fetchSensorData } from '../services/api';

const SensorDetails = ({ deviceId, sensorType }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    fetchSensorData(sensorType, deviceId)
      .then(result => {
        setData(result[0] || {});
        setLoading(false);
      })
      .catch(error => {
        console.error("Error fetching sensor data:", error);
        setLoading(false);
        setData(null);
      });
  }, [deviceId, sensorType]);

  if (loading) {
    return <div className="loading-state-card">Loading data...</div>;
  }

  if (!data || Object.keys(data).length === 0) {
    return <div className="error-state-card">No data found for this sensor.</div>;
  }
  
  // Basic display of live data
  return (
    <div className="sensor-details-card">
      <h3 className="card-title">{sensorType} for {deviceId}</h3>
      <div className="data-grid">
        {Object.entries(data).map(([key, value]) => (
          <div key={key} className="data-item">
            <span className="data-label">{key}:</span>
            <span className="data-value">{String(value)}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SensorDetails;
