import React, { useState } from "react";
import {
  Home,
  Cpu,
  Activity,
  ToggleLeft,
  ToggleRight,
  Zap,
  Battery,
  FileText,
  ChevronDown,
  ChevronUp,
} from "lucide-react";

const menuItems = [
  { label: "Digital Sensors", icon: <Cpu size={18} /> },
  { label: "Analog Sensors", icon: <Activity size={18} /> },
  { label: "Switch Sensors", icon: <ToggleLeft size={18} /> },
  { label: "Internal Relays", icon: <ToggleRight size={18} /> },
  { label: "External Relays", icon: <Zap size={18} /> },
  { label: "Power Sensors", icon: <Battery size={18} /> },
];

const reportItems = [
  { label: "Daily Report", icon: <FileText size={16} /> },
  { label: "Weekly Report", icon: <FileText size={16} /> },
  { label: "Monthly Report", icon: <FileText size={16} /> },
];

const Sidebar = ({
  onSensorSelect,
  onReportSelect,
  onWelcomeSelect,
  activeSensorType,
  selectedPage,
  selectedReport,
}) => {
  const [reportsOpen, setReportsOpen] = useState(false);

  const activeClass =
    "bg-gradient-to-r from-cyan-500 to-blue-500 text-white shadow-lg scale-105 ring-2 ring-cyan-400 animate-pulse";

  return (
    <aside className="relative h-screen w-64 flex flex-col rounded-r-3xl overflow-hidden">
      {/* Animated background */}
      <div className="absolute inset-0 animate-gradient bg-gradient-to-r from-cyan-500 via-blue-500 to-purple-500 opacity-20 z-0"></div>

      {/* Sidebar content */}
      <div className="relative z-10 h-full bg-gray-900/90 text-gray-200 flex flex-col shadow-2xl backdrop-blur-sm border-l-4 border-cyan-400 rounded-r-3xl">
        {/* Header */}
        <div className="p-6 border-b border-gray-700 text-center">
          <h1 className="text-2xl font-extrabold text-white tracking-widest drop-shadow-lg animate-pulse">
            Sensorium
          </h1>
        </div>

        {/* Navigation */}
        <nav className="flex-1 overflow-y-auto mt-4">
          <ul className="space-y-1 px-3">
            {/* Welcome */}
            <li>
              <button
                className={`flex items-center w-full px-4 py-2 rounded-xl text-sm font-medium transition-all duration-300 transform hover:-translate-y-1 hover:shadow-lg ${
                  selectedPage === "welcome"
                    ? activeClass
                    : "text-gray-400 hover:bg-gray-700/30 hover:text-white"
                }`}
                onClick={onWelcomeSelect}
              >
                <Home size={18} className="mr-3 text-cyan-400" />
                <span>Welcome</span>
              </button>
            </li>

            {/* Sensor Items */}
            {menuItems.map(({ label, icon }) => (
              <li key={label}>
                <button
                  className={`flex items-center w-full px-4 py-2 rounded-xl text-sm font-medium transition-all duration-300 transform hover:-translate-y-1 hover:shadow-lg ${
                    activeSensorType === label
                      ? activeClass
                      : "text-gray-400 hover:bg-gray-700/30 hover:text-white"
                  }`}
                  onClick={() => onSensorSelect(label)}
                >
                  <span className="mr-3 text-cyan-400">{icon}</span>
                  {label}
                </button>
              </li>
            ))}

            {/* Reports */}
            <li>
              <button
                className={`flex items-center justify-between w-full px-4 py-2 rounded-xl text-sm font-medium transition-all duration-300 transform hover:-translate-y-1 hover:shadow-lg ${
                  selectedPage === "reports"
                    ? activeClass
                    : "text-gray-400 hover:bg-gray-700/30 hover:text-white"
                }`}
                onClick={() => setReportsOpen(!reportsOpen)}
              >
                <div className="flex items-center">
                  <FileText size={18} className="mr-3 text-cyan-400" />
                  <span>Reports</span>
                </div>
                {reportsOpen ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
              </button>

              {reportsOpen && (
                <ul className="pl-10 mt-1 space-y-1">
                  {reportItems.map(({ label, icon }) => (
                    <li key={label}>
                      <button
                        className={`flex items-center w-full px-3 py-2 rounded-xl text-sm transition-all duration-300 transform hover:-translate-y-1 hover:shadow-lg ${
                          selectedPage === "reports" && selectedReport === label
                            ? activeClass
                            : "text-gray-400 hover:bg-gray-700/30 hover:text-white"
                        }`}
                        onClick={() => onReportSelect(label)}
                      >
                        <span className="mr-2 text-cyan-400">{icon}</span>
                        {label}
                      </button>
                    </li>
                  ))}
                </ul>
              )}
            </li>
          </ul>
        </nav>

        {/* Footer */}
        <div className="p-4 border-t border-gray-700 text-xs text-gray-400 text-center">
          © 2025 GMS · All Rights Reserved
        </div>
      </div>

      {/* Tailwind animation */}
      <style>
        {`
          @keyframes gradientShift {
            0% { background-position: 0% 50%; }
            50% { background-position: 100% 50%; }
            100% { background-position: 0% 50%; }
          }
          .animate-gradient {
            background-size: 200% 200%;
            animation: gradientShift 8s ease infinite;
          }
        `}
      </style>
    </aside>
  );
};

export default Sidebar;
