import React from 'react';
import SensorDetails from './SensorDetails';

const MainContent = ({ selectedDevice, selectedSensor }) => {
  return (
    <main className="main-content">
      <header className="main-header">
        <h2 className="header-title">Live Sensor Data</h2>
        <span className="last-updated">Last updated: {new Date().toLocaleTimeString()}</span>
      </header>

      <div className="content-area">
        {!selectedDevice && !selectedSensor ? (
          <div className="no-sensor-selected-card">
            <span className="icon-placeholder">((.))</span>
            <h3 className="no-sensor-text">No Sensor Selected</h3>
            <p className="no-sensor-subtext">Please select a sensor from the sidebar to view its details.</p>
          </div>
        ) : (
          <SensorDetails 
            deviceId={selectedDevice} 
            sensorType={selectedSensor} 
          />
        )}
      </div>
    </main>
  );
};

export default MainContent;
