import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import moment from 'moment';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';
import './Dashboard.css';

const API_LATEST_URL = 'http://127.0.0.1:5000/api/sensor-data';
const API_HISTORY_URL = 'http://127.0.0.1:5000/api/sensor-history';

// A professional color palette
const COLORS = {
  temperature: '#2c3e50', // Dark Blue
  humidity: '#3498db', // Bright Blue
  heatIndex: '#e67e22', // Orange
  dewPoint: '#95a5a6', // Gray
};

const SensorCard = ({ sensor }) => {
  const [history, setHistory] = useState([]);
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  const fetchHistory = async () => {
    let url = `${API_HISTORY_URL}?sensor_name=${sensor.sensor_name}`;
    if (startDate) {
      url += `&start_date=${moment(startDate).format('YYYY-MM-DD HH:mm:ss')}`;
    }
    if (endDate) {
      url += `&end_date=${moment(endDate).format('YYYY-MM-DD HH:mm:ss')}`;
    }

    try {
      const response = await fetch(url);
      const data = await response.json();
      setHistory(data);
    } catch (error) {
      console.error("Failed to fetch history:", error);
    }
  };

  useEffect(() => {
    fetchHistory();
  }, [startDate, endDate]);

  return (
    <div className="sensor-card">
      <div className="sensor-header">
        <h2>{sensor.sensor_name}</h2>
      </div>
      <div className="sensor-metrics">
        <p>üå°Ô∏è **Temp:** {sensor.temperature}¬∞C</p>
        <p>üíß **Humidity:** {sensor.humidity}%</p>
        <p>üî• **Heat Index:** {sensor.heat_index}¬∞C</p>
        <p>üíß **Dew Point:** {sensor.dew_point}¬∞C</p>
      </div>

      <div className="history-controls">
        <div className="date-picker-group">
          <label>Start Date:</label>
          <DatePicker selected={startDate} onChange={(date) => setStartDate(date)} showTimeSelect dateFormat="Pp" isClearable />
        </div>
        <div className="date-picker-group">
          <label>End Date:</label>
          <DatePicker selected={endDate} onChange={(date) => setEndDate(date)} showTimeSelect dateFormat="Pp" isClearable />
        </div>
      </div>
      
      <div className="sensor-chart">
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={history}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="timestamp" tickFormatter={(timestamp) => moment(timestamp).format('HH:mm')} />
            <YAxis />
            <Tooltip labelFormatter={(timestamp) => `Time: ${moment(timestamp).format('YYYY-MM-DD HH:mm')}`} />
            <Legend />
            <Line type="monotone" dataKey="temperature" stroke={COLORS.temperature} name="Temperature" />
            <Line type="monotone" dataKey="humidity" stroke={COLORS.humidity} name="Humidity" />
            <Line type="monotone" dataKey="heat_index" stroke={COLORS.heatIndex} name="Heat Index" />
            <Line type="monotone" dataKey="dew_point" stroke={COLORS.dewPoint} name="Dew Point" />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

function Dashboard() {
  const [sensorData, setSensorData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(API_LATEST_URL);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setSensorData(data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
    const interval = setInterval(fetchData, 5000);
    return () => clearInterval(interval);
  }, []);

  if (loading) return <div>Loading sensor data...</div>;
  if (error) return <div>Error: {error.message}</div>;

  return (
    <div className="dashboard-container">
      <h1>Room Alert Dashboard</h1>
      <div className="sensors-grid">
        {sensorData.map((sensor) => (
          <SensorCard key={sensor.sensor_name} sensor={sensor} />
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
