import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Welcome.css";

const WelcomePage = () => {
  const [devices, setDevices] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const devicesRes = await axios.get("https://sensorium-api.vercel.app/api/devices");
        const devices = devicesRes.data;

        // For each device, fetch its sensors
        const sensorPromises = devices.map((device) =>
          axios.get(`https://sensorium-api.vercel.app/api/sensors/${device.deviceId}`)
        );

        const sensorResponses = await Promise.all(sensorPromises);

        // Combine device + sensor info
        const devicesWithSensors = devices.map((device, i) => ({
          ...device,
          sensors: sensorResponses[i].data,
        }));

        setDevices(devicesWithSensors);
      } catch (error) {
        console.error("Error fetching devices/sensors:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="welcome-container">
      <h2 className="section-title">Live Sensor Data</h2>

      {devices.map((device) => (
        <div key={device.deviceId} className="device-block">
          <h3 className="device-title">{device.deviceId}</h3>

          <div className="device-meta">
            <p>
              <strong>Serial Number:</strong> {device.serialNumber || "N/A"}
            </p>
            <p>
              <strong>Firmware Version:</strong> {device.firmwareVersion || "N/A"}
            </p>
            <p>
              <strong>IPv4 Address:</strong> {device.ipAddress || "N/A"}
            </p>
            <p>
              <strong>MAC Address:</strong> {device.macAddress || "N/A"}
            </p>
            <p>
              <strong>Port:</strong> {device.port || "N/A"}
            </p>
          </div>

          <div className="sensor-grid">
            {device.sensors.map((sensor, idx) => (
              <div key={idx} className="sensor-card">
                <h4>{sensor.name}</h4>
                <p>
                  <strong>Temperature:</strong>{" "}
                  {sensor.temperature ? `${sensor.temperature} °C` : "N/A"}
                </p>
                <p>
                  <strong>Humidity:</strong>{" "}
                  {sensor.humidity ? `${sensor.humidity} %RH` : "N/A"}
                </p>
                <p>
                  <strong>Heat Index:</strong>{" "}
                  {sensor.heatIndex ? `${sensor.heatIndex} °C` : "N/A"}
                </p>
                <p>
                  <strong>Dew Point:</strong>{" "}
                  {sensor.dewPoint ? `${sensor.dewPoint} °C` : "N/A"}
                </p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default WelcomePage;
