import React, { useState, useEffect } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';
import { FiThermometer, FiDroplet, FiSun, FiCloud } from 'react-icons/fi';
import './Dashboard.css';

const API_LATEST_URL = 'http://127.0.0.1:5000/api/sensor-data';
const API_HISTORY_URL = 'http://127.0.0.1:5000/api/sensor-history';

// Professional color palette
const COLORS = {
  temperature: '#00509d',
  humidity: '#007ba7',
  heatIndex: '#00509d',
  dewPoint: '#7e7e7e',
};

const SensorCard = ({ sensor }) => {
  const [history, setHistory] = useState([]);
  const [activeRange, setActiveRange] = useState('24h');

  const fetchHistory = async (range) => {
    try {
      const url = `${API_HISTORY_URL}?sensor_name=${sensor.sensor_name}&time_range=${range}`;
      const response = await fetch(url);
      const data = await response.json();
      setHistory(data);
    } catch (error) {
      console.error("Failed to fetch history:", error);
    }
  };

  useEffect(() => {
    fetchHistory(activeRange);
  }, [activeRange, sensor.sensor_name]);

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    if (activeRange === '24h') {
      return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    }
    return date.toLocaleDateString();
  };

  return (
    <div className="sensor-card">
      <div className="card-header">
        <h2 className="sensor-name">{sensor.sensor_name}</h2>
      </div>

      <div className="metrics-summary">
        <div className="metric">
          <FiThermometer className="metric-icon temp" />
          <span className="metric-label">Temp</span>
          <span className="metric-value temp">{sensor.temperature}°C</span>
        </div>
        <div className="metric">
          <FiDroplet className="metric-icon humid" />
          <span className="metric-label">Humidity</span>
          <span className="metric-value humid">{sensor.humidity}%</span>
        </div>
        <div className="metric">
          <FiSun className="metric-icon heat-idx" />
          <span className="metric-label">Heat Index</span>
          <span className="metric-value heat-idx">{sensor.heat_index}°C</span>
        </div>
        <div className="metric">
          <FiCloud className="metric-icon dew-pt" />
          <span className="metric-label">Dew Point</span>
          <span className="metric-value dew-pt">{sensor.dew_point}°C</span>
        </div>
      </div>

      <div className="time-range-tabs">
        <button onClick={() => setActiveRange('24h')} className={activeRange === '24h' ? 'active' : ''}>24H</button>
        <button onClick={() => setActiveRange('7d')} className={activeRange === '7d' ? 'active' : ''}>7D</button>
        <button onClick={() => setActiveRange('30d')} className={activeRange === '30d' ? 'active' : ''}>30D</button>
      </div>

      <div className="sensor-chart-container">
        <ResponsiveContainer width="100%" height={250}>
          <LineChart data={history}>
            <CartesianGrid strokeDasharray="3 3" stroke="#e0e0e0" />
            <XAxis dataKey="timestamp" tickFormatter={formatTimestamp} stroke="#bdbdbd" />
            <YAxis stroke="#bdbdbd" />
            <Tooltip />
            <Legend wrapperStyle={{ paddingTop: '10px', fontSize: '12px' }} />
            <Line type="monotone" dataKey="temperature" stroke={COLORS.temperature} name="Temperature" strokeWidth={2} dot={false} />
            <Line type="monotone" dataKey="humidity" stroke={COLORS.humidity} name="Humidity" strokeWidth={2} dot={false} />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

function Dashboard() {
  const [sensorData, setSensorData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(API_LATEST_URL);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setSensorData(data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
    const interval = setInterval(fetchData, 5000);
    return () => clearInterval(interval);
  }, []);

  if (loading) return <div className="loading">Loading sensor data...</div>;
  if (error) return <div className="error">Error: {error.message}</div>;

  return (
    <div className="dashboard-container">
      <div className="dashboard-header-container">
        <h1 className="dashboard-title">Sensor Metrics</h1>
        <p className="dashboard-subtitle">Live and historical data overview.</p>
      </div>
      <div className="sensors-grid">
        {sensorData.map((sensor) => (
          <SensorCard key={sensor.sensor_name} sensor={sensor} />
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
