import React from "react";
import {
  Thermometer,
  Droplets,
  Flame,
  Cloud,
  Power,
  ToggleLeft,
  ToggleRight,
  Wifi,
  Cpu,
  Hash,
  Network,
  Cable,
} from "lucide-react";

const AllSensorsDisplay = ({ allSensorsData, deviceInfo, selectedSensor }) => {
  if (!allSensorsData || allSensorsData.length === 0) {
    return (
      <div className="bg-white shadow-md rounded-lg p-6 text-center text-gray-600">
        No data found for {selectedSensor.toLowerCase()}.
      </div>
    );
  }

  const sensorsByDevice = allSensorsData.reduce((acc, sensor) => {
    const { deviceId } = sensor;
    if (!acc[deviceId]) acc[deviceId] = [];
    acc[deviceId].push(sensor);
    return acc;
  }, {});

  const deviceInfoMap = deviceInfo.reduce((acc, dev) => {
    acc[dev.deviceId] = dev;
    return acc;
  }, {});

  const renderValue = (value) =>
    value !== null && value !== undefined ? value.toFixed(1) : "N/A";

  const renderSensorCard = (sensor, index) => {
    return (
      <div
        key={index}
        className="bg-white shadow-lg rounded-xl p-6 border border-gray-100 hover:shadow-xl transition duration-300"
      >
        {/* Card Header */}
        <div className="flex items-center justify-between mb-4">
          <span className="text-green-500 text-xl font-bold">✓</span>
          <h3 className="text-lg font-semibold text-gray-800 flex items-center gap-2">
            <Cpu size={18} className="text-gray-500" />
            {sensor.label || `Sensor ${sensor.sensorId}`}
          </h3>
        </div>

        {/* === Digital Sensors === */}
        {sensor.temperature !== undefined && sensor.humidity !== undefined && (
          <>
            <div className="mb-4">
              <span className="flex items-center text-sm font-medium text-gray-600 gap-2">
                <Thermometer size={16} className="text-blue-500" /> Temperature
              </span>
              <div className="flex items-center justify-between">
                <span className="text-xl font-bold text-blue-600">
                  {renderValue(sensor.temperature)} °C
                </span>
                <div className="text-xs text-gray-500 space-x-2">
                  <span>High: {renderValue(sensor.temperatureHigh)} °C</span>
                  <span>Low: {renderValue(sensor.temperatureLow)} °C</span>
                </div>
              </div>
            </div>

            <div className="mb-4">
              <span className="flex items-center text-sm font-medium text-gray-600 gap-2">
                <Droplets size={16} className="text-indigo-500" /> Humidity
              </span>
              <div className="flex items-center justify-between">
                <span className="text-xl font-bold text-indigo-600">
                  {renderValue(sensor.humidity)} %RH
                </span>
                <div className="text-xs text-gray-500 space-x-2">
                  <span>High: {renderValue(sensor.humidityHigh)} %RH</span>
                  <span>Low: {renderValue(sensor.humidityLow)} %RH</span>
                </div>
              </div>
            </div>

            {sensor.heatIndex !== undefined && (
              <div className="mb-4">
                <span className="flex items-center text-sm font-medium text-gray-600 gap-2">
                  <Flame size={16} className="text-red-500" /> Heat Index
                </span>
                <div className="flex items-center justify-between">
                  <span className="text-xl font-bold text-red-500">
                    {renderValue(sensor.heatIndex)} °C
                  </span>
                  <div className="text-xs text-gray-500 space-x-2">
                    <span>High: {renderValue(sensor.heatIndexHigh)} °C</span>
                    <span>Low: {renderValue(sensor.heatIndexLow)} °C</span>
                  </div>
                </div>
              </div>
            )}

            {sensor.dewPoint !== undefined && (
              <div className="mb-2">
                <span className="flex items-center text-sm font-medium text-gray-600 gap-2">
                  <Cloud size={16} className="text-cyan-500" /> Dew Point
                </span>
                <span className="text-xl font-bold text-cyan-600">
                  {renderValue(sensor.dewPoint)} °C
                </span>
              </div>
            )}
          </>
        )}

        {/* === Switch Sensors === */}
        {selectedSensor === "Switch Sensors" &&
          sensor.state !== undefined && (
            <div className="mb-2">
              <span className="flex items-center text-sm font-medium text-gray-600 gap-2">
                {sensor.enabled ? (
                  <ToggleRight size={16} className="text-purple-500" />
                ) : (
                  <ToggleLeft size={16} className="text-purple-500" />
                )}
                State
              </span>
              <span className="text-lg font-bold text-purple-600">
                {sensor.enabled === true ? "OPEN" : "CLOSED"}
              </span>
            </div>
          )}

        {/* === Power Sensors === */}
        {selectedSensor === "Power Sensors" &&
          sensor.connected !== undefined && (
            <>
              <div className="mb-2">
                <span className="flex items-center text-sm font-medium text-gray-600 gap-2">
                  <Wifi size={16} className="text-emerald-500" /> Status
                </span>
                <span className="text-lg font-bold text-emerald-600">
                  {sensor.connected === 1 ? "Connected" : "Disconnected"}
                </span>
              </div>
              <div className="mb-2">
                <span className="flex items-center text-sm font-medium text-gray-600 gap-2">
                  <Power size={16} className="text-pink-500" /> State
                </span>
                <span className="text-lg font-bold text-pink-600">
                  {sensor.state === 1 ? "ON" : "OFF"}
                </span>
              </div>
            </>
          )}

        {/* === Relays === */}
        {(selectedSensor === "Internal Relays" ||
          selectedSensor === "External Relays") &&
          sensor.state !== undefined && (
            <div className="mb-2">
              <span className="flex items-center text-sm font-medium text-gray-600 gap-2">
                <Power size={16} className="text-orange-500" /> State
              </span>
              <span className="text-lg font-bold text-orange-600">
                {sensor.state === 1 ? "ON" : "OFF"}
              </span>
            </div>
          )}
      </div>
    );
  };

  return (
    <div className="space-y-8">
      {Object.entries(sensorsByDevice).map(([deviceId, sensors]) => {
        const devInfo = deviceInfoMap[deviceId] || {};

        return (
          <div
            key={deviceId}
            className="bg-gray-50 p-6 rounded-xl shadow-md border border-gray-200"
          >
            <h2 className="text-2xl font-bold text-gray-800 mb-4 flex items-center gap-2">
              <Cpu size={22} className="text-gray-700" />
              {devInfo.deviceName || `Device ${deviceId}`}
            </h2>

            <div className="bg-white rounded-lg shadow-sm p-4 mb-6 grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-5 gap-3 text-sm text-gray-600">
              <p className="flex items-center gap-2">
                <Hash size={14} className="text-gray-500" />
                <strong>Serial:</strong> {devInfo.serialNumber}
              </p>
              <p className="flex items-center gap-2">
                <Cpu size={14} className="text-gray-500" />
                <strong>Firmware:</strong> {devInfo.firmwareVersion}
              </p>
              <p className="flex items-center gap-2">
                <Network size={14} className="text-gray-500" />
                <strong>IPv4:</strong> {devInfo.ipv4Address}
              </p>
              <p className="flex items-center gap-2">
                <Cable size={14} className="text-gray-500" />
                <strong>MAC:</strong> {devInfo.macAddress}
              </p>
              <p className="flex items-center gap-2">
                <Power size={14} className="text-gray-500" />
                <strong>Port:</strong> {devInfo.httpPort}
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {sensors.map((sensor, index) => renderSensorCard(sensor, index))}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default AllSensorsDisplay;
