import React, { useState } from 'react';
import './Sidebar.css';

const menuItems = [
  'Digital Sensors',
  'Analog Sensors',
  'Switch Sensors',
  'Internal Relays',
  'External Relays',
  'Power Sensors'
];

const reportItems = [
  'Daily Report',
  'Weekly Report',
  'Monthly Report',
  'Quarterly Report',
  '6 Months Report',
  'Yearly Report'
];

const Sidebar = ({ onSensorSelect, onReportSelect, activeSensorType }) => {
  const [reportsOpen, setReportsOpen] = useState(false);

  return (
    <aside className="sidebar-container">
      <div className="sidebar-header">
        <h1 className="logo">Dashboard</h1>
      </div>

      <nav className="sidebar-nav">
        <ul className="sidebar-menu">
          {menuItems.map(item => (
            <li key={item} className="sidebar-item">
              <button
                className={`sidebar-button ${activeSensorType === item ? 'active' : ''}`}
                onClick={() => onSensorSelect(item)}
              >
                {item}
              </button>
            </li>
          ))}

          {/* Reports Dropdown */}
          <li className="sidebar-item">
            <button
              className="sidebar-button"
              onClick={() => setReportsOpen(!reportsOpen)}
            >
              Reports {reportsOpen ? '▲' : '▼'}
            </button>

            {reportsOpen && (
              <ul className="sidebar-submenu">
                {reportItems.map(report => (
                  <li key={report} className="sidebar-subitem">
                    <button
                      className="sidebar-subbutton"
                      onClick={() => onReportSelect(report)}
                    >
                      {report}
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </li>
        </ul>
      </nav>

      <div className="sidebar-footer">
        <p>&copy; 2025 GMS</p>
      </div>
    </aside>
  );
};

export default Sidebar;
