import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar/Sidebar";
import MainContent from "./components/MainContent/MainContent";
import SensorLoadingScreen from "./components/Loading/SensorLoadingScreen";
import ReportsPage from "./components/Reports/ReportsPage";
import WelcomePage from "./components/Welcome/Welcome"; 
import "./styles.css";

const App = () => {
  const [selectedPage, setSelectedPage] = useState("welcome"); 
  const [selectedSensor, setSelectedSensor] = useState(null);
  const [selectedReport, setSelectedReport] = useState(null);
  const [loading, setLoading] = useState(true);
  const [reportsOpen, setReportsOpen] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 2000);
    return () => clearTimeout(timer);
  }, []);

  const handleSensorSelect = (sensorType) => {
    setSelectedPage("sensors");
    setSelectedSensor(sensorType);
    setSelectedReport(null);
    setReportsOpen(false);
  };

  const handleReportSelect = (reportType) => {
    setSelectedPage("reports");
    setSelectedReport(reportType);
    setSelectedSensor(null);
    setReportsOpen(true);
  };

  const handleWelcomeSelect = () => {
    setSelectedPage("welcome");
    setSelectedSensor(null);
    setSelectedReport(null);
    setReportsOpen(false);
  };

  return (
    <div className="app-container flex h-screen w-screen bg-gradient-to-br from-gray-100 via-gray-50 to-gray-100">
      {/* Sidebar */}
      <aside className="flex-shrink-0 w-72 lg:w-80 bg-gradient-to-b from-blue-700 via-blue-600 to-blue-800 text-white shadow-xl flex flex-col">
        <Sidebar
          onSensorSelect={handleSensorSelect}
          onReportSelect={handleReportSelect}
          onWelcomeSelect={handleWelcomeSelect}
          activeSensorType={selectedSensor}
          selectedPage={selectedPage}
          selectedReport={selectedReport}
          reportsOpen={reportsOpen}
          setReportsOpen={setReportsOpen}
        />
      </aside>

      {/* Main Content */}
      <main className="flex-1 overflow-auto p-8 relative">
        {loading ? (
          <div className="absolute inset-0 flex items-center justify-center bg-white/70 backdrop-blur-sm z-50 rounded-lg shadow-inner">
            <SensorLoadingScreen />
          </div>
        ) : (
          <div className="space-y-8">
            {selectedPage === "welcome" && (
              <div className="animate-fadeIn">
                <WelcomePage />
              </div>
            )}
            {selectedPage === "reports" && selectedReport && (
              <ReportsPage period={selectedReport} />
            )}
            {selectedPage === "sensors" && selectedSensor && (
              <MainContent selectedSensor={selectedSensor} />
            )}
          </div>
        )}
      </main>
    </div>
  );
};

export default App;
