import React, { useEffect, useState } from "react";
import {
  Thermometer,
  Droplets,
  Activity,
  Server,
  AlertCircle,
} from "lucide-react";
import axios from "axios";

const WelcomePage = () => {
  const [data, setData] = useState({
    totalDevices: 0,
    activeSensors: 0,
    avgTemperature: null,
    avgHumidity: null,
    activeAlerts: 0,
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const devicesRes = await axios.get(
          "https://sensorium-api.vercel.app/api/devices"
        );
        const devices = devicesRes.data;
        const totalDevices = devices.length;

        let activeSensors = 0;
        const sensorPromises = devices.map((device) =>
          axios.get(
            `https://sensorium-api.vercel.app/api/sensors/${device.deviceId}`
          )
        );

        const sensorResponses = await Promise.all(sensorPromises);
        sensorResponses.forEach((res) => {
          activeSensors += res.data.length;
        });

        // Mock values (replace with backend aggregation later)
        const mockAvgTemperature = 22.5;
        const mockAvgHumidity = 65;
        const mockActiveAlerts = 2;

        setData({
          totalDevices,
          activeSensors,
          avgTemperature: mockAvgTemperature,
          avgHumidity: mockAvgHumidity,
          activeAlerts: mockActiveAlerts,
        });
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const statCards = [
    {
      title: "Total Devices",
      value: data.totalDevices,
      icon: <Server size={32} className="text-blue-500" />,
      border: "border-blue-200",
      bg: "bg-gradient-to-br from-blue-50 to-white",
    },
    {
      title: "Active Sensors",
      value: data.activeSensors,
      icon: <Activity size={32} className="text-green-500" />,
      border: "border-green-200",
      bg: "bg-gradient-to-br from-green-50 to-white",
    },
    {
      title: "Avg. Temperature",
      value: data.avgTemperature !== null ? `${data.avgTemperature}Â°C` : "N/A",
      icon: <Thermometer size={32} className="text-red-500" />,
      border: "border-red-200",
      bg: "bg-gradient-to-br from-red-50 to-white",
    },
    {
      title: "Avg. Humidity",
      value: data.avgHumidity !== null ? `${data.avgHumidity}%` : "N/A",
      icon: <Droplets size={32} className="text-purple-500" />,
      border: "border-purple-200",
      bg: "bg-gradient-to-br from-purple-50 to-white",
    },
    {
      title: "Active Alerts",
      value: data.activeAlerts,
      icon: <AlertCircle size={32} className="text-yellow-500" />,
      border: "border-yellow-200",
      bg: "bg-gradient-to-br from-yellow-50 to-white",
    },
  ];

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      {/* Header Section */}
      <div className="mb-12 flex flex-col md:flex-row md:items-center md:justify-between">
        <div>
          <h1 className="text-4xl font-extrabold text-gray-800 tracking-tight">
            Welcome to{" "}
            <span className="text-blue-600 drop-shadow-sm">Sensorium</span>
          </h1>
          <p className="mt-2 text-gray-600 text-lg">
            Your centralized hub for{" "}
            <span className="font-semibold">real-time monitoring</span> and
            intelligent insights.
          </p>
        </div>
        <div className="mt-4 md:mt-0 bg-white border border-gray-200 rounded-lg px-5 py-2 shadow-sm">
          <p className="text-sm text-gray-500">
            Last Sync:{" "}
            <span className="font-medium text-gray-700">
              {new Date().toLocaleTimeString()}
            </span>
          </p>
        </div>
      </div>

      {/* System Overview */}
      <h2 className="text-2xl font-semibold text-gray-700 mb-6 flex items-center">
        <span className="w-1.5 h-6 bg-blue-600 mr-3 rounded"></span>
        System Overview
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-6">
        {statCards.map((card, index) => (
          <div
            key={index}
            className={`flex flex-col items-center justify-center rounded-xl border shadow-md p-6 text-center transition-transform duration-200 hover:-translate-y-1 hover:shadow-lg ${card.bg} ${card.border}`}
          >
            <div className="mb-3 p-3 bg-white rounded-full shadow-sm">
              {card.icon}
            </div>
            <p className="text-sm font-medium text-gray-500">{card.title}</p>
            <h3 className="text-2xl font-bold text-gray-800 mt-1">
              {card.value}
            </h3>
          </div>
        ))}
      </div>

      {/* Reports Section */}
      <div className="mt-14 bg-white shadow-lg border border-gray-200 rounded-xl p-10 text-center">
        <h3 className="text-2xl font-bold text-gray-800 mb-3">
          ðŸ“Š Reports & Analytics
        </h3>
        <p className="text-gray-600 max-w-2xl mx-auto">
          Head to the{" "}
          <span className="font-semibold text-blue-600">Reports</span> section
          to explore{" "}
          <span className="font-medium">historical trends, performance KPIs</span>
          , and interactive visualizations designed to help you make{" "}
          <span className="italic">smarter decisions</span>.
        </p>
      </div>
    </div>
  );
};

export default WelcomePage;
