import React from "react";
import { Server, Thermometer, Droplets, Zap, Activity } from "lucide-react";

const sensorTypeColorMap = {
  "Digital Sensors": "bg-green-50 border-green-200",
  "Analog Sensors": "bg-blue-50 border-blue-200",
  "Switch Sensors": "bg-orange-50 border-orange-200",
  "Internal Relays": "bg-purple-50 border-purple-200",
  "External Relays": "bg-purple-50 border-purple-200",
  "Power Sensors": "bg-pink-50 border-pink-200",
};

const sensorTypeIconMap = {
  "Digital Sensors": <Activity size={16} className="text-green-600" />,
  "Analog Sensors": <Thermometer size={16} className="text-blue-600" />,
  "Switch Sensors": <Zap size={16} className="text-orange-600" />,
  "Internal Relays": <Server size={16} className="text-purple-600" />,
  "External Relays": <Server size={16} className="text-purple-600" />,
  "Power Sensors": <Droplets size={16} className="text-pink-600" />,
};

const AllSensorsDisplay = ({ allSensorsData, deviceInfo, selectedSensor }) => {
  if (!allSensorsData || allSensorsData.length === 0) {
    return (
      <div className="text-center text-black p-6">
        No data found for <span className="font-semibold">{selectedSensor}</span>.
      </div>
    );
  }

  const sensorsByDevice = allSensorsData.reduce((acc, sensor) => {
    const { deviceId } = sensor;
    if (!acc[deviceId]) acc[deviceId] = [];
    acc[deviceId].push(sensor);
    return acc;
  }, {});

  const deviceInfoMap = deviceInfo.reduce((acc, dev) => {
    acc[dev.deviceId] = dev;
    return acc;
  }, {});

  const renderValue = (value) =>
    value !== null && value !== undefined ? value.toFixed(1) : "N/A";

  const renderSensorCard = (sensor, index) => {
    const typeColor = sensorTypeColorMap[selectedSensor] || "bg-gray-50 border-gray-200";
    const typeIcon = sensorTypeIconMap[selectedSensor] || null;

    return (
      <div
        key={index}
        className={`${typeColor} border p-4 transition-all duration-200 hover:shadow-lg`}
      >
        {/* Header */}
        <div className="flex items-center gap-2 mb-3">
          <span
            className={`w-3 h-3 rounded-full ${
              sensor.active ? "bg-green-500 animate-pulse" : "bg-gray-400"
            }`}
          ></span>
          {typeIcon}
          <h3 className="font-semibold text-black">{sensor.label || `Sensor ${sensor.sensorId}`}</h3>
        </div>

        {/* Digital Sensors */}
        {sensor.temperature !== undefined && sensor.humidity !== undefined && (
          <>
            <div className="mb-3">
              <p className="text-sm font-medium text-black">Temperature</p>
              <div className="flex justify-between">
                <span className="text-lg font-bold text-black">{renderValue(sensor.temperature)} °C</span>
                <div className="flex flex-col text-xs text-right">
                  <span className="text-red-600 font-semibold">▲ {renderValue(sensor.temperatureHigh)} °C</span>
                  <span className="text-blue-600 font-semibold">▼ {renderValue(sensor.temperatureLow)} °C</span>
                </div>
              </div>
            </div>

            <div className="mb-3">
              <p className="text-sm font-medium text-black">Humidity</p>
              <div className="flex justify-between">
                <span className="text-lg font-bold text-black">{renderValue(sensor.humidity)} %RH</span>
                <div className="flex flex-col text-xs text-right">
                  <span className="text-red-600 font-semibold">▲ {renderValue(sensor.humidityHigh)} %RH</span>
                  <span className="text-blue-600 font-semibold">▼ {renderValue(sensor.humidityLow)} %RH</span>
                </div>
              </div>
            </div>

            {sensor.heatIndex !== undefined && (
              <div className="mb-3">
                <p className="text-sm font-medium text-black">Heat Index</p>
                <div className="flex justify-between">
                  <span className="text-lg font-bold text-black">{renderValue(sensor.heatIndex)} °C</span>
                  <div className="flex flex-col text-xs text-right">
                    <span className="text-red-600 font-semibold">▲ {renderValue(sensor.heatIndexHigh)} °C</span>
                    <span className="text-blue-600 font-semibold">▼ {renderValue(sensor.heatIndexLow)} °C</span>
                  </div>
                </div>
              </div>
            )}

            {sensor.dewPoint !== undefined && (
              <div>
                <p className="text-sm font-medium text-black">Dew Point</p>
                <span className="text-lg font-bold text-black">{renderValue(sensor.dewPoint)} °C</span>
              </div>
            )}
          </>
        )}

        {/* Other sensor types */}
        {selectedSensor === "Switch Sensors" && sensor.state !== undefined && (
          <div>
            <p className="text-sm font-medium text-black">State</p>
            <span className="text-lg font-bold text-black">{sensor.enabled ? "OPEN" : "CLOSED"}</span>
          </div>
        )}

        {selectedSensor === "Power Sensors" && sensor.connected !== undefined && (
          <>
            <div className="mb-3">
              <p className="text-sm font-medium text-black">Status</p>
              <span className="text-lg font-bold text-black">{sensor.connected === 1 ? "Connected" : "Disconnected"}</span>
            </div>
            <div>
              <p className="text-sm font-medium text-black">State</p>
              <span className="text-lg font-bold text-black">{sensor.state === 1 ? "ON" : "OFF"}</span>
            </div>
          </>
        )}

        {(selectedSensor === "Internal Relays" || selectedSensor === "External Relays") &&
          sensor.state !== undefined && (
            <div>
              <p className="text-sm font-medium text-black">State</p>
              <span className="text-lg font-bold text-black">{sensor.state === 1 ? "ON" : "OFF"}</span>
            </div>
          )}
      </div>
    );
  };

  return (
    <div>
      {Object.entries(sensorsByDevice).map(([deviceId, sensors]) => {
        const devInfo = deviceInfoMap[deviceId] || {};

        return (
          <div key={deviceId} className="mb-8">
            {/* Device Header */}
            <h2 className="text-lg font-bold text-black mb-3 flex items-center gap-2">
              <Server size={16} className="text-blue-600" />
              {devInfo.deviceName || `Device ${deviceId}`}
            </h2>

            {/* Device Info Card */}
            <div className="bg-gray-50 border border-gray-200 p-4 mb-4">
              <div className="grid grid-cols-2 gap-3 text-sm text-black">
                <p><span className="font-semibold">Serial Number:</span> {devInfo.serialNumber}</p>
                <p><span className="font-semibold">Firmware Version:</span> {devInfo.firmwareVersion}</p>
                <p><span className="font-semibold">IPv4 Address:</span> {devInfo.ipv4Address}</p>
                <p><span className="font-semibold">MAC Address:</span> {devInfo.macAddress}</p>
                <p><span className="font-semibold">Port:</span> {devInfo.httpPort}</p>
              </div>
            </div>

            {/* Sensor Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {sensors.map((sensor, index) => renderSensorCard(sensor, index))}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default AllSensorsDisplay;
