import React, { useState } from 'react';
import { LayoutDashboard } from 'lucide-react';

const Sidebar = ({ activeItem, setActiveItem }) => {
  const [reportsOpen, setReportsOpen] = useState(false);

  const menuItems = [
    'Digital Sensors',
    'Analog Sensors',
    'Switch Sensors',
    'Internal Relays',
    'External Relays',
    'Power Sensors'
  ];

  const reportItems = [
    'Daily Report',
    'Weekly Report',
    'Monthly Report'
  ];

  return (
    <aside className="w-64 bg-gray-100 border-r border-gray-300 h-screen p-4 flex flex-col">
      <div className="flex-shrink-0 mb-8">
        <button
          className={`w-full text-left py-2 px-4 rounded-md transition-colors duration-200 ease-in-out flex items-center space-x-2 ${
            activeItem === 'Dashboard'
              ? 'bg-blue-600 text-white font-semibold shadow-md'
              : 'text-gray-600 hover:bg-gray-200'
          }`}
          onClick={() => setActiveItem('Dashboard')}
        >
          <LayoutDashboard className="w-5 h-5" />
          <span>Dashboard</span>
        </button>
      </div>
      <nav className="flex-grow">
        <ul className="space-y-2">
          {menuItems.map(item => (
            <li key={item}>
              <button
                className={`w-full text-left py-2 px-4 rounded-md transition-colors duration-200 ease-in-out text-gray-600 hover:bg-gray-200`}
                onClick={() => setActiveItem(item)}
              >
                {item}
              </button>
            </li>
          ))}
          <li className="space-y-2">
            <button
              className={`w-full text-left py-2 px-4 rounded-md transition-colors duration-200 ease-in-out text-gray-600 hover:bg-gray-200`}
              onClick={() => setReportsOpen(!reportsOpen)}
            >
              Reports {reportsOpen ? '▲' : '▼'}
            </button>
            {reportsOpen && (
              <ul className="ml-4 space-y-1">
                {reportItems.map(report => (
                  <li key={report}>
                    <button
                      className="w-full text-left py-2 px-4 text-sm text-gray-600 hover:bg-gray-200 rounded-md"
                      onClick={() => setActiveItem(report)}
                    >
                      {report}
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </li>
        </ul>
      </nav>
      <div className="sidebar-footer">
        <p className="text-sm text-gray-400 mt-4">&copy; 2025 GMS</p>
      </div>
    </aside>
  );
};

export default Sidebar;
