// src/components/MainContent.js
import React, { useState, useEffect } from 'react';
import SensorDetails from './SensorDetails';
import AllSensorsDisplay from './AllSensorsDisplay'; // New component

const MainContent = ({ selectedDevice, selectedSensor }) => {
  const [allSensorsData, setAllSensorsData] = useState([]);

  useEffect(() => {
    async function fetchAllSensors() {
      try {
        const response = await fetch('http://localhost:5000/api/live/all-sensors');
        if (!response.ok) throw new Error('Failed to fetch all live sensors');
        const data = await response.json();
        setAllSensorsData(data);
      } catch (error) {
        console.error("Error fetching all sensors:", error);
      }
    }
    fetchAllSensors();
  }, []);

  return (
    <main className="main-content">
      <header className="main-header">
        <h2 className="header-title">Live Sensor Data</h2>
        <span className="last-updated">Last updated: {new Date().toLocaleTimeString()}</span>
      </header>
      <div className="content-area">
        {!selectedDevice && !selectedSensor ? (
          <AllSensorsDisplay allSensorsData={allSensorsData} />
        ) : (
          <SensorDetails selectedDevice={selectedDevice} selectedSensor={selectedSensor} />
        )}
      </div>
    </main>
  );
};
export default MainContent;
