import React, { useState } from "react";
import { Info, Thermometer, Droplets, Zap } from "lucide-react";

const AllSensorsDisplay = ({ allSensorsData, deviceInfo, selectedSensor }) => {
  const [expandedDevices, setExpandedDevices] = useState({});

  if (!allSensorsData || allSensorsData.length === 0) {
    return (
      <div className="bg-white rounded-xl shadow p-6 text-center text-gray-500">
        No data found for <span className="font-semibold">{selectedSensor}</span>.
      </div>
    );
  }

  const sensorsByDevice = allSensorsData.reduce((acc, sensor) => {
    const { deviceId } = sensor;
    if (!acc[deviceId]) acc[deviceId] = [];
    acc[deviceId].push(sensor);
    return acc;
  }, {});

  const deviceInfoMap = deviceInfo.reduce((acc, dev) => {
    acc[dev.deviceId] = dev;
    return acc;
  }, {});

  const renderValue = (value) =>
    value !== null && value !== undefined ? value.toFixed(1) : "N/A";

  const toggleDevice = (deviceId) => {
    setExpandedDevices((prev) => ({
      ...prev,
      [deviceId]: !prev[deviceId],
    }));
  };

  const renderSensorCard = (sensor, index) => (
    <div
      key={index}
      className="bg-white rounded-xl shadow-lg border border-gray-200 p-6 hover:shadow-2xl hover:scale-105 transition-all duration-300"
    >
      {/* Header */}
      <div className="flex items-center space-x-2 mb-4 border-b pb-2">
        <span
          className={`w-3 h-3 rounded-full ${
            sensor.active ? "bg-green-500" : "bg-gray-400"
          }`}
        ></span>
        <h3 className="text-lg font-semibold text-gray-800">
          {sensor.label || `Sensor ${sensor.sensorId}`}
        </h3>
      </div>

      {/* Temperature & Humidity */}
      {sensor.temperature !== undefined && sensor.humidity !== undefined && (
        <>
          <div className="mb-4 pb-3 border-b">
            <p className="text-gray-500 text-sm flex items-center gap-1">
              <Thermometer size={16} /> Temperature
            </p>
            <div className="flex items-center justify-between">
              <span className="text-xl font-bold text-red-600">
                {renderValue(sensor.temperature)}°C
              </span>
              <div className="flex flex-col text-xs text-right text-gray-600">
                <span className="text-red-500">▲ {renderValue(sensor.temperatureHigh)}°C</span>
                <span className="text-blue-500">▼ {renderValue(sensor.temperatureLow)}°C</span>
              </div>
            </div>
          </div>

          <div className="mb-4 pb-3 border-b">
            <p className="text-gray-500 text-sm flex items-center gap-1">
              <Droplets size={16} /> Humidity
            </p>
            <div className="flex items-center justify-between">
              <span className="text-xl font-bold text-blue-600">
                {renderValue(sensor.humidity)}% RH
              </span>
              <div className="flex flex-col text-xs text-right text-gray-600">
                <span className="text-red-500">▲ {renderValue(sensor.humidityHigh)}% RH</span>
                <span className="text-blue-500">▼ {renderValue(sensor.humidityLow)}% RH</span>
              </div>
            </div>
          </div>
        </>
      )}

      {/* Other sensor types */}
      {(selectedSensor === "Switch Sensors" || selectedSensor === "Internal Relays" || selectedSensor === "External Relays") &&
        sensor.state !== undefined && (
          <div className="mt-2">
            <p className="text-gray-500 text-sm">State</p>
            <span
              className={`text-xl font-semibold ${
                sensor.state === 1 ? "text-green-600" : "text-red-500"
              }`}
            >
              {sensor.state === 1 ? "ON" : "OFF"}
            </span>
          </div>
        )}

      {selectedSensor === "Power Sensors" && sensor.connected !== undefined && (
        <div className="mt-2">
          <p className="text-gray-500 text-sm">Status</p>
          <span
            className={`text-xl font-semibold ${
              sensor.connected === 1 ? "text-green-600" : "text-red-500"
            }`}
          >
            {sensor.connected === 1 ? "Connected" : "Disconnected"}
          </span>
        </div>
      )}
    </div>
  );

  return (
    <div className="space-y-10">
      {Object.entries(sensorsByDevice).map(([deviceId, sensors]) => {
        const devInfo = deviceInfoMap[deviceId] || {};

        return (
          <div key={deviceId}>
            {/* Device Header */}
            <div
              onClick={() => toggleDevice(deviceId)}
              className="flex justify-between items-center cursor-pointer mb-4"
            >
              <h2 className="text-xl font-semibold text-gray-800 border-b-2 border-gray-300 pb-1">
                {devInfo.deviceName || `Device ${deviceId}`}
              </h2>
              <Info size={20} className="text-gray-400" />
            </div>

            {expandedDevices[deviceId] && (
              <>
                {/* Device Info */}
                <div className="bg-gray-50 rounded-xl shadow-sm border border-gray-200 p-6 mb-6 max-w-3xl">
                  <div className="space-y-2 text-sm text-gray-700">
                    <p>
                      <span className="font-semibold">Serial Number:</span>{" "}
                      {devInfo.serialNumber || "N/A"}
                    </p>
                    <p>
                      <span className="font-semibold">Firmware Version:</span>{" "}
                      {devInfo.firmwareVersion || "N/A"}
                    </p>
                    <p>
                      <span className="font-semibold">IPv4 Address:</span>{" "}
                      {devInfo.ipv4Address || "N/A"}
                    </p>
                    <p>
                      <span className="font-semibold">MAC Address:</span>{" "}
                      {devInfo.macAddress || "N/A"}
                    </p>
                    <p>
                      <span className="font-semibold">Port:</span>{" "}
                      {devInfo.httpPort || "N/A"}
                    </p>
                  </div>
                </div>

                {/* Sensor Cards */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {sensors.map((sensor, index) => renderSensorCard(sensor, index))}
                </div>
              </>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default AllSensorsDisplay;
