import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar/Sidebar";
import MainContent from "./components/MainContent/MainContent";
import SensorLoadingScreen from "./components/Loading/SensorLoadingScreen";
import ReportsPage from "./components/Reports/ReportsPage";
import WelcomePage from "./components/Welcome/Welcome"; // ðŸ‘ˆ add this
import "./styles.css";

const App = () => {
  const [selectedPage, setSelectedPage] = useState("welcome"); // ðŸ‘ˆ default page = welcome
  const [selectedSensor, setSelectedSensor] = useState("Digital Sensors");
  const [selectedReport, setSelectedReport] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 2000);
    return () => clearTimeout(timer);
  }, []);

  const handleSensorSelect = (sensorType) => {
    setSelectedPage("sensors");
    setSelectedSensor(sensorType);
    setSelectedReport(null); // reset report view when switching sensors
  };

  const handleReportSelect = (reportType) => {
    setSelectedPage("reports");
    setSelectedReport(reportType);
  };

  if (loading) {
    return <SensorLoadingScreen />;
  }

  return (
    <div className="app-container">
      <Sidebar
        onSensorSelect={handleSensorSelect}
        onReportSelect={handleReportSelect}
        activeSensorType={selectedSensor}
        onWelcomeSelect={() => setSelectedPage("welcome")} // ðŸ‘ˆ allow going back to welcome
      />

      <main className="main-content">
        {selectedPage === "welcome" && <WelcomePage />}
        {selectedPage === "reports" && selectedReport && (
          <ReportsPage period={selectedReport} />
        )}
        {selectedPage === "sensors" && (
          <MainContent selectedSensor={selectedSensor} />
        )}
      </main>
    </div>
  );
};

export default App;
