import React, { useEffect, useState } from "react";
import { Thermometer, Droplets, Activity, Server, AlertCircle } from "lucide-react";
import "./App.css"; // Import external CSS

// Base URL for the API
const API_BASE_URL = "https://sensorium-api.vercel.app/api";

const App = () => {
  const [loading, setLoading] = useState(true);
  const [overviewData, setOverviewData] = useState({
    totalDevices: 0,
    activeSensors: 0,
    avgTemperature: null,
    avgHumidity: null,
    activeAlerts: 0,
  });
  const [sensorData, setSensorData] = useState([]);

  // Function to fetch all data from the APIs
  const fetchAllData = async () => {
    setLoading(true);
    try {
      const devicesResponse = await fetch(`${API_BASE_URL}/live/devices`);
      if (!devicesResponse.ok) {
        throw new Error(`Failed to fetch devices: ${devicesResponse.statusText}`);
      }
      const devices = await devicesResponse.json();

      const digitalSensorsResponse = await fetch(
        `${API_BASE_URL}/live/digital-sensors`
      );
      if (!digitalSensorsResponse.ok) {
        throw new Error(
          `Failed to fetch digital sensors: ${digitalSensorsResponse.statusText}`
        );
      }

      const digitalSensors = await digitalSensorsResponse.json();

      // Keep only sensors with temperature, humidity, heatIndex, and dewPoint
      const filteredSensors = digitalSensors.map((sensor) => {
        const { label, temperature, humidity, heatIndex, dewPoint } = sensor;
        return { label, temperature, humidity, heatIndex, dewPoint };
      });

      setSensorData(filteredSensors);

      const totalDevices = devices.length;
      const activeSensors = filteredSensors.length;

      setOverviewData({
        totalDevices,
        activeSensors,
        avgTemperature: null,
        avgHumidity: null,
        activeAlerts: 2,
      });
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAllData();
    const intervalId = setInterval(() => {
      fetchAllData();
    }, 5000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="app-container">
      <div className="main-wrapper">
        {/* Header */}
        <header className="header">
          <div>
            <h1 className="title">Welcome to Sensorium</h1>
            <p className="subtitle">
              Your centralized hub for real-time sensor monitoring.
            </p>
          </div>
        </header>

        {/* Loading State */}
        {loading ? (
          <div className="loading">
            <h2>Loading live data...</h2>
            <p>This may take a moment.</p>
          </div>
        ) : (
          <>
            {/* System Overview */}
            <section>
              <h2 className="section-title">System Overview</h2>
              <div className="stats-grid">
                <div className="stat-card">
                  <Server size={32} />
                  <p>Total Devices</p>
                  <h3>{overviewData.totalDevices}</h3>
                </div>
                <div className="stat-card">
                  <Activity size={32} />
                  <p>Active Sensors</p>
                  <h3>{overviewData.activeSensors}</h3>
                </div>
                <div className="stat-card">
                  <Thermometer size={32} />
                  <p>Avg. Temperature</p>
                  <h3>
                    {overviewData.avgTemperature !== null
                      ? `${overviewData.avgTemperature}°C`
                      : "N/A"}
                  </h3>
                </div>
                <div className="stat-card">
                  <Droplets size={32} />
                  <p>Avg. Humidity</p>
                  <h3>
                    {overviewData.avgHumidity !== null
                      ? `${overviewData.avgHumidity}%`
                      : "N/A"}
                  </h3>
                </div>
                <div className="stat-card">
                  <AlertCircle size={32} />
                  <p>Active Alerts</p>
                  <h3>{overviewData.activeAlerts}</h3>
                </div>
              </div>
            </section>

            {/* Sensors Section */}
            <section>
              <h2 className="section-title">Live Digital Sensor Data</h2>
              <div className="sensor-grid">
                {sensorData.map((sensor, index) => (
                  <div key={index} className="sensor-card">
                    <h3 className="sensor-title">{sensor.label}</h3>
                    <div className="sensor-data">
                      {sensor.temperature !== undefined && (
                        <div className="sensor-item">
                          <p>Temperature</p>
                          <span>{sensor.temperature} °C</span>
                        </div>
                      )}
                      {sensor.humidity !== undefined && (
                        <div className="sensor-item">
                          <p>Humidity</p>
                          <span>{sensor.humidity} %</span>
                        </div>
                      )}
                      {sensor.heatIndex !== undefined && (
                        <div className="sensor-item">
                          <p>Heat Index</p>
                          <span>{sensor.heatIndex} °C</span>
                        </div>
                      )}
                      {sensor.dewPoint !== undefined && (
                        <div className="sensor-item">
                          <p>Dew Point</p>
                          <span>{sensor.dewPoint} °C</span>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </section>
          </>
        )}

        {/* Placeholder */}
        <div className="placeholder">
          <h3>Detailed Reports and Analytics</h3>
          <p>
            Navigate to the reports section to view historical data and detailed
            graphs.
          </p>
        </div>
      </div>
    </div>
  );
};

export default App;
