import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import {
  Thermometer,
  Droplets,
  Activity,
  Cloud,
  Calendar,
  Server,
  Cpu,
} from "lucide-react";
import "./ReportsPage.css";

const ReportPage = ({ period }) => {
  const [devices, setDevices] = useState([]);
  const [sensors, setSensors] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState("");
  const [selectedSensor, setSelectedSensor] = useState("");
  const [reportData, setReportData] = useState([]);

  // Fetch devices
  useEffect(() => {
    axios.get("https://sensorium-api.vercel.app/api/devices").then((res) => {
      setDevices(res.data);
    });
  }, []);

  // Fetch sensors when device changes
  useEffect(() => {
    if (selectedDevice) {
      axios
        .get(`https://sensorium-api.vercel.app/api/sensors/${selectedDevice}`)
        .then((res) => {
          setSensors(res.data);
          setSelectedSensor("");
        });
    } else {
      setSensors([]);
      setSelectedSensor("");
    }
  }, [selectedDevice]);

  // Map frontend period names to backend period names
  const periodMap = {
    "Daily Report": "daily",
    "Weekly Report": "weekly",
    "Monthly Report": "monthly",
  };

  // Fetch report when device/sensor/period changes
  useEffect(() => {
    if (selectedDevice && selectedSensor && period) {
      const backendPeriod = periodMap[period];
      if (!backendPeriod) {
        setReportData([]);
        return;
      }
      axios
        .get(
          `https://sensorium-api.vercel.app/api/reports?deviceId=${selectedDevice}&sensorId=${selectedSensor}&period=${backendPeriod}`
        )
        .then((res) => {
          setReportData(res.data);
        })
        .catch((error) => {
          console.error("Error fetching report:", error);
          setReportData([]);
        });
    }
  }, [selectedDevice, selectedSensor, period]);

  // A helper variable to determine if we should show the daily/detailed table
  const isDailyReport = period === "Daily Report";

  return (
    <div className="report-container">
      <h2 className="report-title flex items-center gap-2">
        <Calendar className="w-6 h-6 text-blue-600" />
        Reports - {period}
      </h2>

      {/* Filters */}
      <div className="filters">
        <div className="filter-box">
          <Server className="w-5 h-5 text-gray-500 mr-2" />
          <select
            className="dropdown"
            value={selectedDevice}
            onChange={(e) => setSelectedDevice(e.target.value)}
          >
            <option value="">Select Device</option>
            {devices.map((d) => (
              <option key={d.deviceId} value={d.deviceId}>
                {d.deviceName}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-box">
          <Cpu className="w-5 h-5 text-gray-500 mr-2" />
          <select
            className="dropdown"
            value={selectedSensor}
            onChange={(e) => setSelectedSensor(e.target.value)}
            disabled={!selectedDevice}
          >
            <option value="">Select Sensor</option>
            {sensors.map((s) => (
              <option key={s.sensorId} value={s.sensorId}>
                {s.label || `Sensor ${s.sensorId}`}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Conditionally render the correct table based on the report type */}
      {reportData.length > 0 && isDailyReport ? (
        // Daily Report (Raw Data) Table
        <>
          <h3 className="sub-heading">Daily Report (Raw Data)</h3>
          <div className="table-wrapper">
            <table className="report-table">
              <thead>
                <tr>
                  <th><Calendar className="inline w-4 h-4 mr-1" /> Timestamp</th>
                  <th><Thermometer className="inline w-4 h-4 mr-1" /> Temperature</th>
                  <th><Droplets className="inline w-4 h-4 mr-1" /> Humidity</th>
                  <th><Activity className="inline w-4 h-4 mr-1" /> Heat Index</th>
                  <th><Cloud className="inline w-4 h-4 mr-1" /> Dew Point</th>
                </tr>
              </thead>
              <tbody>
                {reportData.map((row, idx) => (
                  <tr key={idx}>
                    <td>{new Date(row.period).toLocaleString()}</td>
                    <td>{row.temperature !== null ? row.temperature.toFixed(2) : "-"}</td>
                    <td>{row.humidity !== null ? row.humidity.toFixed(2) : "-"}</td>
                    <td>{row.heatIndex !== null ? row.heatIndex.toFixed(2) : "-"}</td>
                    <td>{row.dewPoint !== null ? row.dewPoint.toFixed(2) : "-"}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      ) : reportData.length > 0 ? (
        // Weekly/Monthly Report (Aggregated Data) Table & Graph
        <>
          <h3 className="sub-heading">Aggregated Report (Daily Averages)</h3>
          <div className="table-wrapper">
            <table className="report-table">
              <thead>
                <tr>
                  <th>
                    <Calendar className="inline w-4 h-4 mr-1" /> Day
                  </th>
                  <th>
                    <Thermometer className="inline w-4 h-4 mr-1" /> Min Temp
                  </th>
                  <th>
                    <Thermometer className="inline w-4 h-4 mr-1" /> Avg Temp
                  </th>
                  <th>
                    <Thermometer className="inline w-4 h-4 mr-1" /> Max Temp
                  </th>
                  <th>
                    <Droplets className="inline w-4 h-4 mr-1" /> Avg Humidity
                  </th>
                  <th>
                    <Activity className="inline w-4 h-4 mr-1" /> Avg Heat Index
                  </th>
                  <th>
                    <Cloud className="inline w-4 h-4 mr-1" /> Avg Dew Point
                  </th>
                </tr>
              </thead>
              <tbody>
                {reportData.map((row, idx) => (
                  <tr key={idx}>
                    <td>{new Date(row.period).toLocaleDateString()}</td>
                    <td>{row.minTemp !== null ? row.minTemp.toFixed(2) : "-"}</td>
                    <td>{row.avgTemp !== null ? row.avgTemp.toFixed(2) : "-"}</td>
                    <td>{row.maxTemp !== null ? row.maxTemp.toFixed(2) : "-"}</td>
                    <td>{row.avgHumidity !== null ? row.avgHumidity.toFixed(2) : "-"}</td>
                    <td>{row.avgHeatIndex !== null ? row.avgHeatIndex.toFixed(2) : "-"}</td>
                    <td>{row.avgDewPoint !== null ? row.avgDewPoint.toFixed(2) : "-"}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      ) : (
        // No data message
        <p className="no-data-message">Select a device and sensor to view the report.</p>
      )}

      {/* Graph for Aggregated Reports only */}
      {reportData.length > 0 && !isDailyReport && (
        <div className="chart-wrapper">
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={reportData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis
                dataKey="period"
                tickFormatter={(date) => new Date(date).toLocaleDateString("en-GB")}
              />
              <YAxis />
              <Tooltip
                labelFormatter={(date) => new Date(date).toLocaleDateString("en-GB")}
              />
              <Legend />
              <Line
                type="monotone"
                dataKey="avgTemp"
                stroke="#007bff"
                name="Avg Temp"
              />
              <Line
                type="monotone"
                dataKey="avgHumidity"
                stroke="#28a745"
                name="Avg Humidity"
              />
              <Line
                type="monotone"
                dataKey="avgHeatIndex"
                stroke="#ff5722"
                name="Avg Heat Index"
              />
              <Line
                type="monotone"
                dataKey="avgDewPoint"
                stroke="#6f42c1"
                name="Avg Dew Point"
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
};

export default ReportPage;
