import React from "react";

const SensorLoadingScreen = () => {
  return (
    <div className="fixed inset-0 flex items-center justify-center bg-gray-950 bg-opacity-70 backdrop-blur-sm z-50">
      <div className="flex flex-col items-center space-y-6">
        {/* Modern Spinner */}
        <div className="relative w-16 h-16">
          <div className="absolute inset-0 rounded-full border-4 border-gray-700"></div>
          <div className="absolute inset-0 rounded-full border-4 border-blue-500 border-t-transparent animate-spin"></div>
        </div>

        {/* Elegant Loading Text */}
        <div className="flex items-center text-lg font-semibold text-gray-200 tracking-wide">
          <span>Connecting to Sensors</span>
          <span className="ml-1 flex space-x-1">
            <span className="animate-bounce">.</span>
            <span className="animate-bounce delay-150">.</span>
            <span className="animate-bounce delay-300">.</span>
          </span>
        </div>
      </div>
    </div>
  );
};

export default SensorLoadingScreen;
