import React, { useEffect, useState } from 'react';
import { Thermometer, Droplets, Activity, Cloud, Server, AlertCircle } from 'lucide-react';
import axios from 'axios';

const WelcomePage = () => {
  const [data, setData] = useState({
    totalDevices: 0,
    activeSensors: 0,
    avgTemperature: null,
    avgHumidity: null,
    activeAlerts: 0,
  });

  // Fetch real data from the API and calculate stats
  useEffect(() => {
    const fetchData = async () => {
      try {
        const devicesRes = await axios.get("https://sensorium-api.vercel.app/api/devices");
        const devices = devicesRes.data;
        const totalDevices = devices.length;

        let activeSensors = 0;
        
        const sensorPromises = devices.map(device =>
          axios.get(`https://sensorium-api.vercel.app/api/sensors/${device.deviceId}`)
        );
        
        const sensorResponses = await Promise.all(sensorPromises);
        sensorResponses.forEach(res => {
          activeSensors += res.data.length;
        });

        // Mocking the alerts and averages for this demonstration
        const mockAvgTemperature = 22.5;
        const mockAvgHumidity = 65;
        const mockActiveAlerts = 2;

        setData({
          totalDevices,
          activeSensors,
          avgTemperature: mockAvgTemperature,
          avgHumidity: mockAvgHumidity,
          activeAlerts: mockActiveAlerts,
        });

      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const statCards = [
    {
      title: 'Total Devices',
      value: data.totalDevices,
      icon: <Server className="w-8 h-8 text-blue-500" />,
      color: 'bg-blue-100',
    },
    {
      title: 'Active Sensors',
      value: data.activeSensors,
      icon: <Activity className="w-8 h-8 text-green-500" />,
      color: 'bg-green-100',
    },
    {
      title: 'Avg. Temperature',
      value: data.avgTemperature !== null ? `${data.avgTemperature}°C` : 'N/A',
      icon: <Thermometer className="w-8 h-8 text-red-500" />,
      color: 'bg-red-100',
    },
    {
      title: 'Avg. Humidity',
      value: data.avgHumidity !== null ? `${data.avgHumidity}%` : 'N/A',
      icon: <Droplets className="w-8 h-8 text-indigo-500" />,
      color: 'bg-indigo-100',
    },
    {
      title: 'Active Alerts',
      value: data.activeAlerts,
      icon: <AlertCircle className="w-8 h-8 text-yellow-500" />,
      color: 'bg-yellow-100',
    },
  ];

  return (
    <div className="p-4 sm:p-6 md:p-8 bg-gray-50 min-h-screen">
      <div className="max-w-7xl mx-auto">
        {/* Header Section */}
        <div className="bg-white rounded-2xl shadow-lg p-6 sm:p-8 flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4 mb-8">
          <div>
            <h1 className="text-3xl sm:text-4xl font-extrabold text-gray-800">
              Welcome to Sensorium
            </h1>
            <p className="text-sm sm:text-base text-gray-600 mt-1">
              Your centralized hub for real-time sensor monitoring.
            </p>
          </div>
        </div>

        {/* At-a-Glance Statistics */}
        <h2 className="text-2xl font-bold text-gray-800 mb-6">
          System Overview
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-6">
          {statCards.map((card, index) => (
            <div
              key={index}
              className="bg-white rounded-2xl shadow-md p-6 flex flex-col items-start transition-transform duration-300 hover:scale-105 hover:shadow-xl"
            >
              <div
                className={`w-14 h-14 rounded-full flex items-center justify-center ${card.color}`}
              >
                {card.icon}
              </div>
              <p className="text-sm text-gray-500 font-medium mt-4">
                {card.title}
              </p>
              <h3 className="text-3xl font-extrabold text-gray-900 mt-1">
                {card.value}
              </h3>
            </div>
          ))}
        </div>

        {/* Placeholder for future sections */}
        <div className="mt-12">
          <div className="bg-white rounded-2xl shadow-md p-8 text-center text-gray-500">
            <h3 className="text-xl font-semibold mb-2">Detailed Reports and Analytics</h3>
            <p>
              Navigate to the reports section to view historical data and detailed graphs.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WelcomePage;
