import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

const ReportPage = ({ period }) => {
  const [devices, setDevices] = useState([]);
  const [sensors, setSensors] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState("");
  const [selectedSensor, setSelectedSensor] = useState("");
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/devices").then((res) => {
      setDevices(res.data);
    });
  }, []);

  useEffect(() => {
    if (selectedDevice) {
      axios
        .get(`http://localhost:5000/api/sensors/${selectedDevice}`)
        .then((res) => {
          setSensors(res.data);
        });
    }
  }, [selectedDevice]);

  const fetchReport = () => {
    if (!selectedDevice || !selectedSensor) return;
    axios
      .get(
        `http://localhost:5000/api/reports?deviceId=${selectedDevice}&sensorId=${selectedSensor}&period=${period}`
      )
      .then((res) => {
        setData(res.data);
      });
  };

  return (
    <div className="p-6 flex flex-col gap-6">
      <h2 className="text-2xl font-bold text-blue-600">Reports - {period}</h2>

      {/* Filters */}
      <div className="flex gap-4">
        <select
          className="border rounded p-2"
          onChange={(e) => setSelectedDevice(e.target.value)}
        >
          <option value="">Select Device</option>
          {devices.map((d) => (
            <option key={d.deviceId} value={d.deviceId}>
              Device {d.deviceId}
            </option>
          ))}
        </select>

        <select
          className="border rounded p-2"
          onChange={(e) => setSelectedSensor(e.target.value)}
          disabled={!selectedDevice}
        >
          <option value="">Select Sensor</option>
          {sensors.map((s) => (
            <option key={s.sensorId} value={s.sensorId}>
              {s.label || `Sensor ${s.sensorId}`}
            </option>
          ))}
        </select>

        <button
          className="bg-blue-600 text-white px-4 py-2 rounded"
          onClick={fetchReport}
        >
          Generate
        </button>
      </div>

      {/* Table */}
      {data.length > 0 && (
        <table className="table-auto border-collapse border w-full">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-4 py-2">Period</th>
              <th className="border px-4 py-2">Min Temp</th>
              <th className="border px-4 py-2">Avg Temp</th>
              <th className="border px-4 py-2">Max Temp</th>
              <th className="border px-4 py-2">Avg Humidity</th>
              <th className="border px-4 py-2">Avg Heat Index</th>
              <th className="border px-4 py-2">Avg Dew Point</th>
            </tr>
          </thead>
          <tbody>
            {data.map((row, idx) => (
              <tr key={idx}>
                <td className="border px-4 py-2">{row.period}</td>
                <td className="border px-4 py-2">{row.minTemp}</td>
                <td className="border px-4 py-2">{row.avgTemp}</td>
                <td className="border px-4 py-2">{row.maxTemp}</td>
                <td className="border px-4 py-2">{row.avgHumidity}</td>
                <td className="border px-4 py-2">{row.avgHeatIndex}</td>
                <td className="border px-4 py-2">{row.avgDewPoint}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Graph */}
      {data.length > 0 && (
        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="period" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="avgTemp" stroke="#007bff" />
            <Line type="monotone" dataKey="avgHumidity" stroke="#28a745" />
            <Line type="monotone" dataKey="avgHeatIndex" stroke="#ff5722" />
            <Line type="monotone" dataKey="avgDewPoint" stroke="#6f42c1" />
          </LineChart>
        </ResponsiveContainer>
      )}
    </div>
  );
};

export default ReportPage;
