import React, { useEffect, useState } from "react";
import {
  Thermometer,
  Droplets,
  Activity,
  Server,
  AlertCircle,
  RefreshCw,
} from "lucide-react";
import axios from "axios";

const WelcomePage = () => {
  const [data, setData] = useState({
    totalDevices: 0,
    activeSensors: 0,
    avgTemperature: null,
    avgHumidity: null,
    activeAlerts: 0,
  });
  const [lastSync, setLastSync] = useState(new Date());

  const fetchData = async () => {
    try {
      const devicesRes = await axios.get(
        "https://sensorium-api.vercel.app/api/devices"
      );
      const devices = devicesRes.data;
      const totalDevices = devices.length;

      let activeSensors = 0;
      const sensorPromises = devices.map((device) =>
        axios.get(
          `https://sensorium-api.vercel.app/api/sensors/${device.deviceId}`
        )
      );

      const sensorResponses = await Promise.all(sensorPromises);
      sensorResponses.forEach((res) => {
        activeSensors += res.data.length;
      });

      const mockAvgTemperature = 22.5;
      const mockAvgHumidity = 65;
      const mockActiveAlerts = 2;

      setData({
        totalDevices,
        activeSensors,
        avgTemperature: mockAvgTemperature,
        avgHumidity: mockAvgHumidity,
        activeAlerts: mockActiveAlerts,
      });
      setLastSync(new Date());
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const statCards = [
    {
      title: "Total Devices",
      value: data.totalDevices,
      icon: <Server size={28} className="text-blue-600" />,
      color: "from-blue-100 to-blue-50",
    },
    {
      title: "Active Sensors",
      value: data.activeSensors,
      icon: <Activity size={28} className="text-green-600" />,
      color: "from-green-100 to-green-50",
    },
    {
      title: "Avg. Temperature",
      value: data.avgTemperature !== null ? `${data.avgTemperature}Â°C` : "N/A",
      icon: <Thermometer size={28} className="text-red-600" />,
      color: "from-red-100 to-red-50",
    },
    {
      title: "Avg. Humidity",
      value: data.avgHumidity !== null ? `${data.avgHumidity}%` : "N/A",
      icon: <Droplets size={28} className="text-purple-600" />,
      color: "from-purple-100 to-purple-50",
    },
    {
      title: "Active Alerts",
      value: data.activeAlerts,
      icon: <AlertCircle size={28} className="text-yellow-600" />,
      color: "from-yellow-100 to-yellow-50",
    },
  ];

  return (
    <div className="min-h-screen p-8 bg-gradient-to-br from-white via-gray-50 to-white">
      {/* Header */}
      <div className="mb-12 flex flex-col lg:flex-row lg:items-center lg:justify-between gap-6">
        <div>
          <h1 className="text-4xl lg:text-5xl font-extrabold text-gray-800 tracking-tight flex flex-wrap">
            <span className="inline-block opacity-0 animate-fadeUp">
              Welcome
            </span>
            <span className="inline-block opacity-0 animate-fadeUpDelay200 ml-2">
              to
            </span>
            <span className="inline-block text-blue-600 drop-shadow-md opacity-0 animate-fadeUpDelay400 ml-2">
              Sensorium
            </span>
          </h1>
          <p className="mt-2 text-gray-600 text-lg lg:text-xl">
            Your centralized hub for{" "}
            <span className="font-semibold">real-time monitoring</span> and
            intelligent insights.
          </p>
        </div>
        <div className="flex items-center gap-4">
          <p className="text-sm text-gray-700">
            Last Sync:{" "}
            <span className="font-medium">{lastSync.toLocaleTimeString()}</span>
          </p>
          <button
            onClick={fetchData}
            className="p-2 bg-blue-50 hover:bg-blue-100 transition rounded"
            title="Refresh"
          >
            <RefreshCw size={18} className="text-blue-600" />
          </button>
        </div>
      </div>

      {/* Overview Section */}
      <h2 className="text-2xl font-semibold text-gray-700 mb-6 flex items-center">
        <span className="w-1.5 h-6 bg-blue-600 mr-3 rounded-full"></span>
        System Overview
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-6">
        {statCards.map((card, index) => (
          <div
            key={index}
            className={`flex flex-col items-center justify-center rounded-xl border shadow-lg p-6 text-center transition-transform duration-300 hover:-translate-y-2 hover:shadow-2xl bg-gradient-to-br ${card.color}`}
          >
            <div className="mb-4 p-4 bg-white rounded-full shadow-md flex items-center justify-center animate-pulseSlow">
              {card.icon}
            </div>
            <p className="text-sm font-medium text-gray-500">{card.title}</p>
            <h3 className="text-2xl font-bold text-gray-800 mt-1">{card.value}</h3>
          </div>
        ))}
      </div>

      {/* Reports Section */}
      <div className="mt-16 bg-white shadow-xl border border-gray-200 rounded-xl p-10 text-center hover:shadow-2xl transition-shadow">
        <h3 className="text-2xl lg:text-3xl font-bold text-gray-800 mb-4">
          ðŸ“Š Reports & Analytics
        </h3>
        <p className="text-gray-600 max-w-2xl mx-auto leading-relaxed text-lg">
          Navigate to the reports section to view historical data and detailed graphs{" "}
          <span className="font-semibold text-blue-600">historical trends</span>,{" "}
          <span className="font-medium">performance KPIs</span>, and interactive
          visualizations designed to help you make{" "}
          <span className="italic">smarter decisions</span>.
        </p>
      </div>
    </div>
  );
};

export default WelcomePage;
