import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import {
  Thermometer,
  Droplets,
  Activity,
  Cloud,
  Calendar,
  Server,
  Cpu,
} from "lucide-react";

// Load Tailwind CSS and configuration before the component.
const TailwindConfigScript = () => {
  const tailwindScript = `
    tailwind.config = {
      theme: {
        extend: {
          fontFamily: {
            sans: ['Inter', 'sans-serif'],
          },
        },
      },
    };
  `;
  return (
    <>
      <script src="https://cdn.tailwindcss.com"></script>
      <script dangerouslySetInnerHTML={{ __html: tailwindScript }}></script>
      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css"></link>
    </>
  );
};

// Main App component
export default function ReportsPage() {
  const [period, setPeriod] = useState("daily"); // State to manage the selected period
  
  return (
    <div className="min-h-screen bg-gray-100 p-4 font-sans text-gray-800">
      <TailwindConfigScript />
      <div className="max-w-7xl mx-auto rounded-lg shadow-lg bg-white p-6 md:p-10">
        <header className="flex flex-col md:flex-row items-center justify-between border-b pb-4 mb-6">
          <div className="flex items-center gap-4">
            <h1 className="text-3xl font-bold text-gray-900">Sensorium Dashboard</h1>
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-500">
                <Server className="inline-block w-4 h-4 mr-1" />
                25 devices online
              </span>
              <span className="text-sm text-gray-500">
                <Cloud className="inline-block w-4 h-4 mr-1" />
                API Health: Good
              </span>
            </div>
          </div>
          <div className="mt-4 md:mt-0 flex gap-2 p-1 bg-gray-200 rounded-full">
            <button
              onClick={() => setPeriod("daily")}
              className={`py-1 px-4 rounded-full text-sm font-medium transition-all ${
                period === "daily" ? "bg-white shadow-sm text-blue-600" : "text-gray-600 hover:bg-gray-300"
              }`}
            >
              Daily
            </button>
            <button
              onClick={() => setPeriod("weekly")}
              className={`py-1 px-4 rounded-full text-sm font-medium transition-all ${
                period === "weekly" ? "bg-white shadow-sm text-blue-600" : "text-gray-600 hover:bg-gray-300"
              }`}
            >
              Weekly
            </button>
            <button
              onClick={() => setPeriod("monthly")}
              className={`py-1 px-4 rounded-full text-sm font-medium transition-all ${
                period === "monthly" ? "bg-white shadow-sm text-blue-600" : "text-gray-600 hover:bg-gray-300"
              }`}
            >
              Monthly
            </button>
          </div>
        </header>

        <ReportPage period={period} />
      </div>
    </div>
  );
}

// Provided ReportPage component, now integrated into the single file
const ReportPage = ({ period }) => {
  const [devices, setDevices] = useState([]);
  const [sensors, setSensors] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState("");
  const [selectedSensor, setSelectedSensor] = useState("");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [summaryData, setSummaryData] = useState(null);

  // Fetch devices
  useEffect(() => {
    setLoading(true);
    axios.get("https://sensorium-api.vercel.app/api/devices")
      .then((res) => {
        setDevices(res.data);
      })
      .catch(err => {
        console.error("Failed to fetch devices:", err);
        setError("Failed to load devices.");
      })
      .finally(() => setLoading(false));
  }, []);

  // Fetch sensors when device changes
  useEffect(() => {
    if (selectedDevice) {
      setLoading(true);
      axios.get(`https://sensorium-api.vercel.app/api/sensors/${selectedDevice}`)
        .then((res) => {
          setSensors(res.data);
          setSelectedSensor(""); // Reset sensor selection
        })
        .catch(err => {
          console.error("Failed to fetch sensors:", err);
          setError("Failed to load sensors for the selected device.");
        })
        .finally(() => setLoading(false));
    } else {
      setSensors([]);
      setSelectedSensor("");
    }
  }, [selectedDevice]);

  // Auto-fetch report when device/sensor/period changes
  useEffect(() => {
    if (selectedDevice && selectedSensor) {
      setLoading(true);
      setError(null);
      axios.get(
        `https://sensorium-api.vercel.app/api/reports?deviceId=${selectedDevice}&sensorId=${selectedSensor}&period=${period}`
      )
        .then((res) => {
          // If daily, filter for current date (midnight â†’ now)
          if (period === "daily") {
            const today = new Date().toISOString().split("T")[0]; // YYYY-MM-DD
            const filtered = res.data.filter(
              (row) => new Date(row.period).toISOString().split("T")[0] === today
            );
            setData(filtered);
          } else {
            setData(res.data);
          }
        })
        .catch(err => {
          console.error("Failed to fetch report:", err);
          setError("Failed to load report data.");
        })
        .finally(() => setLoading(false));
    } else {
      setData([]);
    }
  }, [selectedDevice, selectedSensor, period]);

  // Calculate summary data when main data changes
  useEffect(() => {
    if (data.length > 0) {
      const totals = data.reduce((acc, curr) => {
        acc.avgTemp += curr.avgTemp || 0;
        acc.avgHumidity += curr.avgHumidity || 0;
        acc.avgHeatIndex += curr.avgHeatIndex || 0;
        acc.avgDewPoint += curr.avgDewPoint || 0;
        return acc;
      }, { avgTemp: 0, avgHumidity: 0, avgHeatIndex: 0, avgDewPoint: 0 });
      
      setSummaryData({
        avgTemp: totals.avgTemp / data.length,
        avgHumidity: totals.avgHumidity / data.length,
        avgHeatIndex: totals.avgHeatIndex / data.length,
        avgDewPoint: totals.avgDewPoint / data.length,
      });
    } else {
      setSummaryData(null);
    }
  }, [data]);

  const getSensorName = (id) => {
    const sensor = sensors.find(s => s.sensorId === id);
    return sensor ? sensor.label : `Sensor ${id}`;
  };

  const renderIcon = (dataKey) => {
    switch(dataKey) {
      case 'avgTemp': return <Thermometer className="inline w-4 h-4 mr-1" />;
      case 'avgHumidity': return <Droplets className="inline w-4 h-4 mr-1" />;
      case 'avgHeatIndex': return <Activity className="inline w-4 h-4 mr-1" />;
      case 'avgDewPoint': return <Cloud className="inline w-4 h-4 mr-1" />;
      default: return null;
    }
  };

  return (
    <div className="report-container">
      <h2 className="report-title flex items-center gap-2 text-xl font-semibold text-gray-700 mb-6">
        <Calendar className="w-6 h-6 text-blue-600" />
        {getSensorName(selectedSensor) || "Reports"} - {period}
      </h2>

      {/* Filters */}
      <div className="flex flex-col md:flex-row gap-4 mb-8">
        <div className="filter-box flex items-center bg-gray-50 rounded-lg p-3 w-full md:w-1/2">
          <Server className="w-5 h-5 text-gray-500 mr-2" />
          <select
            className="dropdown w-full bg-transparent outline-none text-gray-700"
            value={selectedDevice}
            onChange={(e) => setSelectedDevice(e.target.value)}
          >
            <option value="">Select Device</option>
            {devices.map((d) => (
              <option key={d.deviceId} value={d.deviceId}>
                {d.deviceName}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-box flex items-center bg-gray-50 rounded-lg p-3 w-full md:w-1/2">
          <Cpu className="w-5 h-5 text-gray-500 mr-2" />
          <select
            className="dropdown w-full bg-transparent outline-none text-gray-700"
            value={selectedSensor}
            onChange={(e) => setSelectedSensor(e.target.value)}
            disabled={!selectedDevice}
          >
            <option value="">Select Sensor</option>
            {sensors.map((s) => (
              <option key={s.sensorId} value={s.sensorId}>
                {s.label || `Sensor ${s.sensorId}`}
              </option>
            ))}
          </select>
        </div>
      </div>

      {loading && <div className="text-center text-gray-500 my-8">Loading data...</div>}
      {error && <div className="text-center text-red-500 my-8">{error}</div>}
      {selectedDevice && selectedSensor && !loading && data.length === 0 && (
        <div className="text-center text-gray-500 my-8">
          No data available for the selected period.
        </div>
      )}

      {/* Summary Cards */}
      {summaryData && (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          <div className="bg-white p-6 rounded-lg shadow-sm flex items-center gap-4">
            <div className="bg-blue-100 p-3 rounded-full">
              <Thermometer className="w-8 h-8 text-blue-600" />
            </div>
            <div>
              <p className="text-sm font-medium text-gray-500">Avg Temp</p>
              <p className="text-2xl font-bold text-gray-900">{summaryData.avgTemp.toFixed(2)} Â°C</p>
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-sm flex items-center gap-4">
            <div className="bg-green-100 p-3 rounded-full">
              <Droplets className="w-8 h-8 text-green-600" />
            </div>
            <div>
              <p className="text-sm font-medium text-gray-500">Avg Humidity</p>
              <p className="text-2xl font-bold text-gray-900">{summaryData.avgHumidity.toFixed(2)} %</p>
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-sm flex items-center gap-4">
            <div className="bg-orange-100 p-3 rounded-full">
              <Activity className="w-8 h-8 text-orange-600" />
            </div>
            <div>
              <p className="text-sm font-medium text-gray-500">Avg Heat Index</p>
              <p className="text-2xl font-bold text-gray-900">{summaryData.avgHeatIndex.toFixed(2)} Â°C</p>
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-sm flex items-center gap-4">
            <div className="bg-purple-100 p-3 rounded-full">
              <Cloud className="w-8 h-8 text-purple-600" />
            </div>
            <div>
              <p className="text-sm font-medium text-gray-500">Avg Dew Point</p>
              <p className="text-2xl font-bold text-gray-900">{summaryData.avgDewPoint.toFixed(2)} Â°C</p>
            </div>
          </div>
        </div>
      )}

      {/* Table & Graph */}
      {data.length > 0 && (
        <>
          {/* Graph */}
          <div className="chart-wrapper bg-white p-6 rounded-lg shadow-sm mb-8">
            <h3 className="text-lg font-semibold mb-4 border-b pb-2">Sensor Trends</h3>
            <ResponsiveContainer width="100%" height={400}>
              <LineChart data={data}>
                <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                <XAxis
                  dataKey="period"
                  tickFormatter={(date) =>
                    period === "daily"
                      ? new Date(date).toLocaleTimeString("en-GB")
                      : new Date(date).toLocaleDateString("en-GB")
                  }
                  angle={-30}
                  textAnchor="end"
                  height={50}
                  stroke="#6b7280"
                />
                <YAxis stroke="#6b7280" />
                <Tooltip
                  labelFormatter={(date) =>
                    period === "daily"
                      ? new Date(date).toLocaleTimeString("en-GB")
                      : new Date(date).toLocaleDateString("en-GB")
                  }
                />
                <Legend />
                <Line
                  type="monotone"
                  dataKey="avgTemp"
                  stroke="#1c75bc"
                  name="Avg Temp"
                  dot={false}
                  strokeWidth={2}
                />
                <Line
                  type="monotone"
                  dataKey="avgHumidity"
                  stroke="#28a745"
                  name="Avg Humidity"
                  dot={false}
                  strokeWidth={2}
                />
                <Line
                  type="monotone"
                  dataKey="avgHeatIndex"
                  stroke="#ff5722"
                  name="Avg Heat Index"
                  dot={false}
                  strokeWidth={2}
                />
                <Line
                  type="monotone"
                  dataKey="avgDewPoint"
                  stroke="#6f42c1"
                  name="Avg Dew Point"
                  dot={false}
                  strokeWidth={2}
                />
              </LineChart>
            </ResponsiveContainer>
          </div>

          {/* Table */}
          <div className="table-wrapper overflow-x-auto bg-white rounded-lg shadow-sm">
            <h3 className="text-lg font-semibold px-6 pt-6 border-b pb-2">Detailed Data</h3>
            <table className="report-table w-full text-left table-auto">
              <thead className="text-gray-600 bg-gray-50">
                <tr>
                  <th className="py-3 px-6"><Calendar className="inline w-4 h-4 mr-1" /> Period</th>
                  <th className="py-3 px-6">
                    <Thermometer className="inline w-4 h-4 mr-1" /> Min Temp
                  </th>
                  <th className="py-3 px-6">
                    <Thermometer className="inline w-4 h-4 mr-1" /> Avg Temp
                  </th>
                  <th className="py-3 px-6">
                    <Thermometer className="inline w-4 h-4 mr-1" /> Max Temp
                  </th>
                  <th className="py-3 px-6">
                    <Droplets className="inline w-4 h-4 mr-1" /> Avg Humidity
                  </th>
                  <th className="py-3 px-6">
                    <Activity className="inline w-4 h-4 mr-1" /> Avg Heat Index
                  </th>
                  <th className="py-3 px-6">
                    <Cloud className="inline w-4 h-4 mr-1" /> Avg Dew Point
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {data.map((row, idx) => (
                  <tr key={idx} className="hover:bg-gray-50">
                    <td className="py-4 px-6 text-gray-900 font-medium whitespace-nowrap">
                      {period === "daily"
                        ? new Date(row.period).toLocaleTimeString()
                        : new Date(row.period).toLocaleDateString()}
                    </td>
                    <td className="py-4 px-6">{row.minTemp !== null ? row.minTemp.toFixed(2) : "-"}</td>
                    <td className="py-4 px-6">{row.avgTemp !== null ? row.avgTemp.toFixed(2) : "-"}</td>
                    <td className="py-4 px-6">{row.maxTemp !== null ? row.maxTemp.toFixed(2) : "-"}</td>
                    <td className="py-4 px-6">{row.avgHumidity !== null ? row.avgHumidity.toFixed(2) : "-"}</td>
                    <td className="py-4 px-6">{row.avgHeatIndex !== null ? row.avgHeatIndex.toFixed(2) : "-"}</td>
                    <td className="py-4 px-6">{row.avgDewPoint !== null ? row.avgDewPoint.toFixed(2) : "-"}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
};
