import React, { useEffect, useState } from 'react';
import { DashboardHeader } from "@/components/DashboardHeader";
import { TemperatureSensor } from "@/components/TemperatureSensor";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { ArrowRight, Zap, Thermometer, Droplets, Activity, Cloud, Server, AlertCircle } from "lucide-react";
import axios from 'axios';

const Welcome = () => {
  const [data, setData] = useState({
    totalDevices: 0,
    activeSensors: 0,
    avgTemperature: null,
    avgHumidity: null,
    activeAlerts: 0,
  });

  const [sensorData, setSensorData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch devices
        const devicesRes = await axios.get("https://sensorium-api.vercel.app/api/devices");
        const devices = devicesRes.data;
        const totalDevices = devices.length;

        let activeSensors = 0;
        let allSensorsData = [];
        let totalTemp = 0;
        let totalHumidity = 0;
        let tempCount = 0;
        let humidityCount = 0;
        
        // Fetch sensors for each device
        const sensorPromises = devices.map(device =>
          axios.get(`https://sensorium-api.vercel.app/api/sensors/${device.deviceId}`)
            .then(res => ({ deviceId: device.deviceId, deviceName: device.deviceName, sensors: res.data }))
        );
        
        const sensorResponses = await Promise.all(sensorPromises);
        
        // Process sensor data
        sensorResponses.forEach(deviceData => {
          deviceData.sensors.forEach((sensor, index) => {
            activeSensors++;
            
            // Transform sensor data to match our component format
            const sensorInfo = {
              id: sensor.sensorId || `${deviceData.deviceId}_${index}`,
              name: sensor.label || `${deviceData.deviceName} Sensor ${index + 1}`,
              location: deviceData.deviceName,
              initialTemp: 20 + Math.random() * 15, // Random temp for demo
              isOnline: true,
              deviceId: deviceData.deviceId
            };
            
            allSensorsData.push(sensorInfo);
            
            // Calculate averages (using random values for demo since API might not have live readings)
            totalTemp += sensorInfo.initialTemp;
            totalHumidity += 50 + Math.random() * 30; // Random humidity
            tempCount++;
            humidityCount++;
          });
        });

        const avgTemperature = tempCount > 0 ? (totalTemp / tempCount).toFixed(1) : null;
        const avgHumidity = humidityCount > 0 ? (totalHumidity / humidityCount).toFixed(1) : null;
        const activeAlerts = Math.floor(Math.random() * 5); // Mock alerts

        setData({
          totalDevices,
          activeSensors,
          avgTemperature: parseFloat(avgTemperature),
          avgHumidity: parseFloat(avgHumidity),
          activeAlerts,
        });

        setSensorData(allSensorsData);

      } catch (error) {
        console.error("Error fetching data:", error);
        // Fallback to empty data on error
        setData({
          totalDevices: 0,
          activeSensors: 0,
          avgTemperature: null,
          avgHumidity: null,
          activeAlerts: 0,
        });
        setSensorData([]);
      }
    };

    fetchData();
  }, []);

  const statCards = [
    {
      title: 'Total Devices',
      value: data.totalDevices,
      icon: Server,
      color: 'text-primary',
      bgColor: 'bg-primary/10',
    },
    {
      title: 'Active Sensors',
      value: data.activeSensors,
      icon: Activity,
      color: 'text-green-500',
      bgColor: 'bg-green-500/10',
    },
    {
      title: 'Avg. Temperature',
      value: data.avgTemperature !== null ? `${data.avgTemperature}°C` : 'N/A',
      icon: Thermometer,
      color: 'text-red-500',
      bgColor: 'bg-red-500/10',
    },
    {
      title: 'Avg. Humidity',
      value: data.avgHumidity !== null ? `${data.avgHumidity}%` : 'N/A',
      icon: Droplets,
      color: 'text-blue-500',
      bgColor: 'bg-blue-500/10',
    },
    {
      title: 'Active Alerts',
      value: data.activeAlerts,
      icon: AlertCircle,
      color: 'text-yellow-500',
      bgColor: 'bg-yellow-500/10',
    },
  ];

  return (
    <div className="min-h-screen bg-background">
      <DashboardHeader />
      
      {/* Hero Section */}
      <section className="hero-gradient px-6 py-16">
        <div className="max-w-6xl mx-auto text-center">
          <div className="inline-flex items-center gap-2 bg-primary/10 text-primary px-4 py-2 rounded-full text-sm font-medium mb-6">
            <Zap className="h-4 w-4" />
            Real-time IoT Monitoring
          </div>
          
          <h1 className="text-4xl md:text-6xl font-bold mb-6">
            Welcome to{" "}
            <span className="bg-gradient-to-r from-primary to-accent bg-clip-text text-transparent">
              Sensorium
            </span>
          </h1>
          
          <p className="text-xl text-muted-foreground mb-8 max-w-3xl mx-auto">
            Your centralized hub for real-time sensor monitoring. 
            Get instant alerts, track trends, and maintain optimal conditions across all your facilities.
          </p>
          
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Button size="lg" className="primary-gradient text-primary-foreground">
              View Live Dashboard
              <ArrowRight className="ml-2 h-4 w-4" />
            </Button>
            <Button variant="outline" size="lg">
              System Overview
            </Button>
          </div>
        </div>
      </section>

      {/* Main Dashboard Content */}
      <main className="max-w-7xl mx-auto px-6 py-12">
        {/* System Overview Stats */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold mb-6">System Overview</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-6 mb-12">
            {statCards.map((card, index) => {
              const IconComponent = card.icon;
              return (
                <Card key={index} className="sensor-card p-6 hover:scale-105 transition-transform duration-300">
                  <div className="flex flex-col items-start">
                    <div className={`w-14 h-14 rounded-full flex items-center justify-center ${card.bgColor} mb-4`}>
                      <IconComponent className={`h-8 w-8 ${card.color}`} />
                    </div>
                    <p className="text-sm text-muted-foreground font-medium">
                      {card.title}
                    </p>
                    <h3 className="text-3xl font-bold text-card-foreground mt-1">
                      {card.value}
                    </h3>
                  </div>
                </Card>
              );
            })}
          </div>
        </div>
        
        <div className="mb-8">
          <h2 className="text-2xl font-bold mb-2">Temperature Sensors</h2>
          <p className="text-muted-foreground">Live monitoring of all temperature sensors across your facilities</p>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-12">
          {sensorData.length > 0 ? (
            sensorData.map((sensor) => (
              <TemperatureSensor
                key={sensor.id}
                id={sensor.id}
                name={sensor.name}
                location={sensor.location}
                initialTemp={sensor.initialTemp}
                isOnline={sensor.isOnline}
              />
            ))
          ) : (
            <div className="col-span-full text-center py-8">
              <p className="text-muted-foreground">Loading sensors...</p>
            </div>
          )}
        </div>

        {/* Quick Actions */}
        <Card className="sensor-card p-8">
          <div className="text-center">
            <h3 className="text-xl font-semibold mb-4">Quick Actions</h3>
            <div className="flex flex-wrap gap-4 justify-center">
              <Button variant="outline">Add New Sensor</Button>
              <Button variant="outline">Configure Alerts</Button>
              <Button variant="outline">Export Data</Button>
              <Button variant="outline">System Settings</Button>
            </div>
          </div>
        </Card>
      </main>
    </div>
  );
};

export default Welcome;
